INSERT INTO `tbl_product_recipes` (`id`, `product_id`, `name`, `subject`, `url`, `synopsis`, `text`, `media`, `video`, `background`, `cover`, `attribute`, `allow_comment`, `favorited`, `served`, `time`, `messages`, `tags`, `order`, `user_id`, `count`, `status`, `added`, `modified`) VALUES
(1, 4, NULL, 'New Gado Gado', 'new-gado-gado', '<p class="bodyText_Type_Color" itemprop="description" style="font-size: 14px; line-height: 17px;">\n	If you&rsquo;re a fan of mayonnaise, this salad is a must-try recipe.<br />\n	But don&rsquo;t forget to use it with KEWPIE MAYONNAISE</p>\n<p class="bodyText_Type_Color" style="font-size: 14px; line-height: 17px;">\n	JAPANESE STYLE for that extra tasty, unique flavour.</p>\n', '<ul>\n	<li>\n		Potato</li>\n	<li>\n		Carrots</li>\n	<li>\n		Boiled Eggs</li>\n	<li>\n		Prawn Crackers</li>\n	<li>\n		Cabbage</li>\n	<li>\n		Bean sprouts</li>\n	<li>\n		Rice cake</li>\n	<li>\n		KEWPIE SALAD DRESSING ROASTED SESAME</li>\n</ul>\n', '0c7ae-preview-samall.jpg', NULL, NULL, NULL, '<p>\n	Better served while Hot</p>\n', NULL, 'yes', '1', '12', '<ol>\n	<li>\n		Boil cabbage, bean sprouts, eggs, carrots and potatoes.</li>\n	<li>\n		Cut rice cake.</li>\n	<li>\n		Fry shrimp crackers.</li>\n	<li>\n		Serve all ingredients on a plate</li>\n	<li>\n		Pour with Kewpie Salad Dressing Roasted Sesame.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432737062, 1433396661),
(2, 5, NULL, 'Seafood Salad', 'seafood-salad', '<p>\n	Seafood Salad</p>\n', '<ul>\n	<li>\n		shrimp</li>\n	<li>\n		squid</li>\n	<li>\n		salmon</li>\n	<li>\n		lettuce</li>\n	<li>\n		Cherry tomatoes</li>\n	<li>\n		Yellow peppers</li>\n	<li>\n		Kewpie Salad Dressing Soy Sauce</li>\n</ul>\n', '66df3-preview-small.jpg', NULL, NULL, NULL, '<p>\n	Better served while hot</p>\n', NULL, 'yes', '2', '18', '<ol>\n	<li>\n		Boil shrimp and squid.</li>\n	<li>\n		Grill squid on a griddle.</li>\n	<li>\n		Cut peppers and cherry tomatoes to garnish.</li>\n	<li>\n		Put lettuce on a plate, arrange all the ingredients on top of it</li>\n	<li>\n		Pour with Kewpie salad dressing.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432809110, 1433350326),
(3, 6, NULL, 'Buffalo Chicken Salad', 'buffalo-chicken-salad', '<p>\n	Buffalo Chicken Salad</p>\n', '<ul>\n	<li>\n		Fried chicken wing</li>\n	<li>\n		Tomato</li>\n	<li>\n		Lettuce</li>\n	<li>\n		Onion Leaves</li>\n	<li>\n		Lemonade</li>\n	<li>\n		Kewpie Salad Dressing Spicy Tomato&nbsp;</li>\n</ul>\n', '776d7-small-preview.jpg', NULL, NULL, NULL, '<p>\n	Better served hot</p>\n', NULL, 'yes', '3', '19', '<ol>\n	<li>\n		Arrange lettuce on a plate.</li>\n	<li>\n		Place the fried chicken on it.</li>\n	<li>\n		Put lime juice and red onion to garnish</li>\n	<li>\n		Pour Kewpie Salad Dressing on it</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432809399, 1433350439),
(4, 7, NULL, 'American Caesar Salad', 'american-caesar-salad', '<p>\n	American Caesar Salad</p>\n', '<ul>\n	<li>\n		Grilled chicken (breast)</li>\n	<li>\n		Olive (cut round)</li>\n	<li>\n		Crouton</li>\n	<li>\n		Romaine Lettuce</li>\n	<li>\n		Kewpie Salad Dressing Caesar&nbsp;</li>\n</ul>\n', '2a0d6-preview-small.jpg', NULL, NULL, NULL, '<p>\n	Served hot will be perfect</p>\n', NULL, 'yes', '3', '20', '<ol>\n	<li>\n		Roast chicken.</li>\n	<li>\n		Cut olives in round shape</li>\n	<li>\n		Rip lettuce.</li>\n	<li>\n		Place the chicken and olives on top the lettuce.</li>\n	<li>\n		Pour Kewpie salad dressing on it.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432818546, 1433350555),
(5, 8, NULL, 'Boiled Salad', 'boiled-salad', '<p>\n	Boiled Salad</p>\n', '<ul>\n	<li>\n		shrimp</li>\n	<li>\n		egg</li>\n	<li>\n		broccoli</li>\n	<li>\n		carrots</li>\n	<li>\n		Potato</li>\n	<li>\n		Kewpie Salad Dressing Thousand Islands</li>\n</ul>\n', 'cd851-preview-small.jpg', NULL, NULL, NULL, '<p>\n	Better served while hot</p>\n', NULL, 'no', '5', '25', '<ol>\n	<li>\n		Boiled shrimp, broccoli, carrots, potatoes and eggs.</li>\n	<li>\n		Arrange on a plate</li>\n	<li>\n		Pour salad dressing over the arrangement.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432821748, 1433350642),
(6, 10, NULL, 'Hot Dog', 'hot-dog', '<p>\n	Hot Dog</p>\n', '<ul>\n	<li>\n		hotdog bun</li>\n	<li>\n		chicken sausage</li>\n	<li>\n		Pickles</li>\n	<li>\n		lettuce</li>\n	<li>\n		chili sauce</li>\n	<li>\n		Kewpie Sandwich Spread Cheese</li>\n</ul>\n', NULL, NULL, NULL, NULL, '<p>\n	Better served while Hot</p>\n', NULL, 'no', '5', '35', '<ol>\n	<li>\n		Place the lettuce in hotdog bun.</li>\n	<li>\n		Add pickles.</li>\n	<li>\n		Place chicken sausage.</li>\n	<li>\n		Add chili sauce (if desired) and coat sausage with sandwich spread on it.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'unpublish', 1432829829, 1433350726),
(7, 1, NULL, 'Fried Chicken with Mayo Dip', 'fried-chicken-with-mayo-dip', '<p>\n	Fried Chicken with Mayo Dip</p>\n', '<ul>\n	<li>\n		Fried Chicken</li>\n	<li>\n		carrots</li>\n	<li>\n		lettuce</li>\n	<li>\n		broccoli</li>\n	<li>\n		Baby potatoes</li>\n	<li>\n		Lime</li>\n	<li>\n		Kewpie Mayonnaise Original</li>\n</ul>\n<p>\n	&nbsp;</p>\n', '574f8-preview-small.jpg', NULL, NULL, NULL, '<p>\n	Better served while HOT</p>\n', NULL, '', '3', '15', '<ol>\n	<li>\n		Boil carrots, broccoli and potatoes.</li>\n	<li>\n		Arrange lettuce on a plate.</li>\n	<li>\n		Serve carrots, broccoli, potatoes and fried chicken on a plate.</li>\n	<li>\n		Add mayonnaise as condiment</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432911778, 1433350798),
(8, 2, NULL, 'Easy Mayo Dips', 'easy-mayo-dips', '<p>\n	Easy Mayo Dips</p>\n', '<ul>\n	<li>\n		Fritters</li>\n	<li>\n		Fried cassava</li>\n	<li>\n		Fried Tempe</li>\n	<li>\n		Kewpie Mayonnaise Original Sweet&nbsp;</li>\n</ul>\n<p>\n	&nbsp;</p>\n', NULL, NULL, NULL, NULL, '<p>\n	Better served while hot</p>\n', NULL, '', '2', '15', '<p>\n	Serve fritter, fried cassava and fried tempeh with sweet mayonnaise.</p>\n', NULL, NULL, NULL, NULL, 'unpublish', 1432911889, 1433350885),
(9, 3, NULL, 'Ebi Fry', 'ebi-fry', '<p>\n	Ebi Fry</p>\n', '<ul>\n	<li>\n		Ebi Fry</li>\n	<li>\n		cucumber</li>\n	<li>\n		tomatoes</li>\n	<li>\n		Sweet soy sauce (optional)</li>\n	<li>\n		lime</li>\n	<li>\n		Cabbage</li>\n	<li>\n		KEWPIE MAYONNAISE SAVORY</li>\n</ul>\n', NULL, NULL, NULL, NULL, NULL, NULL, '', '5', '15', '<ol>\n	<li>\n		Thinly slice cabbage.</li>\n	<li>\n		Put ebi fry over cabbage.</li>\n	<li>\n		Cut tomato, cucumber and lime, decorate all on the plate.</li>\n	<li>\n		Add soy sauce and Kewpie Mayo Savory</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'unpublish', 1432911997, 1433350923),
(10, 1, NULL, 'Egg Sandwich', 'egg-sandwich', '<p>\n	Egg Sandwich</p>\n', '<ul>\n	<li>\n		3 Slices Crustless Bread</li>\n	<li>\n		2 Boiled Eggs</li>\n	<li>\n		Salt to taste</li>\n	<li>\n		2 tbs Kewpie mayonnaise Original</li>\n</ul>\n', 'cb1c9-preview.jpg', NULL, NULL, NULL, '<p>\n	Well served while hot</p>\n', NULL, 'no', '2', '23', '<ol>\n	<li>\n		Put boiled eggs into a bowl. Mash using fork until they become crumbles. Add salt and mayonnaise, mix until blended.</li>\n	<li>\n		Prepare the bread, fill with mayonnaise as you like it, add boiled egg mixtures. Repeat to all breads, then cut them into triangles. Sandwiches are ready to be served.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432912861, 1433351099),
(11, 1, NULL, '2 Bread Rolls', '2-bread-rolls', '<p>\n	2 Bread Rolls</p>\n', '<ul>\n	<li>\n		8 slices Crustless Bread</li>\n	<li>\n		2 slices Smoked Beef</li>\n	<li>\n		4 Lettuces</li>\n	<li>\n		&frac12; nori sheet</li>\n	<li>\n		80 gr Canned tuna</li>\n	<li>\n		Kewpie Mayonnaise Original</li>\n</ul>\n', '78a8e-preview.jpg', NULL, NULL, NULL, '<p>\n	Well served while hot</p>\n', NULL, '', '2', '15', '<p>\n	(To Nori and Smoked Beef Sandwich)</p>\n<ol>\n	<li>\n		Cut nori into 4 parts.</li>\n	<li>\n		Spread mayonnaise on the bread, put the ingredients in this order: 2 pieces of lettuce that has been kneaded, the put smoked beef on top of it. Roll and cut diagonally into 2 parts.</li>\n</ol>\n<p>\n	(Untuk Tuna Mayo Roll)</p>\n<ol>\n	<li>\n		Mix tuna and mayonnaise to make a tuna mayo.</li>\n	<li>\n		Apply tuna mayo on the bread in this order:&nbsp; place the lettuce that has been kneaded and tuna on top of it, roll and cut diagonally into 2 parts.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432913145, 1433351041),
(12, 1, NULL, 'Egg Salad On Sandwich', 'egg-salad-on-sandwich', '<p>\n	Egg Salad on Sandwich</p>\n', '<ul>\n	<li>\n		2 slices white bread</li>\n	<li>\n		2 lettuces</li>\n	<li>\n		2 eggs</li>\n	<li>\n		1 Beef Bacon</li>\n	<li>\n		cooking oil</li>\n	<li>\n		Kewpie mayonnaise original</li>\n	<li>\n		*Bacon can be replaced with smoked beef</li>\n</ul>\n', '58e7c-preview-small.jpg', NULL, NULL, NULL, '<p>\n	Well served while Hot</p>\n', NULL, '', '2', '35', '<ol>\n	<li>\n		Wash the lettuce with cold water, then drain. Rip to an edible size.</li>\n	<li>\n		Cut the bacon approximately 2 cm, sauteed in the pan without oil. Add oil in the same skillet, wait until hot, put beaten egg and stir till cooked.</li>\n	<li>\n		Put lettuce, bacon and egg at the top of the bread, add mayonnaise on it.</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432913333, 1433351165),
(13, 1, NULL, 'Broccoli Sandwich & Sauteed Mayo Eggs', 'broccoli-sandwich-sauteed-mayo-eggs', '<p>\n	Broccoli Sandwich &amp; Sauteed Mayo Eggs</p>\n', '<ul>\n	<li>\n		6 slices Bread</li>\n	<li>\n		&frac12; head Broccoli</li>\n	<li>\n		2 slices Beef Bacon</li>\n	<li>\n		2 eggs</li>\n	<li>\n		pepper to taste</li>\n	<li>\n		&frac12; tbsp Kewpie mayonnaise Original&nbsp;</li>\n</ul>\n', '4bd16-preview-small.jpg', NULL, NULL, NULL, '<p>\n	Well served while hot</p>\n', NULL, '', '2', '23', '<ol>\n	<li>\n		6 slices Bread</li>\n	<li>\n		&frac12; head Broccoli</li>\n	<li>\n		2 slices Beef Bacon</li>\n	<li>\n		2 eggs</li>\n	<li>\n		pepper to taste</li>\n	<li>\n		&frac12; tbsp Kewpie mayonnaise Original</li>\n</ol>\n', NULL, NULL, NULL, NULL, 'publish', 1432913459, 1433351247);
