INSERT INTO `tbl_translations` (`id`, `table`, `field_id`, `lang_id`, `name`, `subject`, `url`, `synopsis`, `text`, `attribute`, `messages`, `user_id`, `count`, `status`, `added`, `modified`) VALUES
(1, 'tbl_pages', 1, 1, NULL, 'Kini Kewpie Hadir di Indonesia!', 'kini-kewpie-hadir-di-indonesia', NULL, '<p>\n	Dengan pengalaman selama 90 tahun di Jepang, Kewpie menghadirkan produk-produk&nbsp;lezat dan bermanfaat untuk Anda di Indonesia.</p>\n', NULL, NULL, 1, NULL, 1, 1432742665, 1432889087),
(2, 'tbl_pages', 8, 1, NULL, 'Mayonais', 'mayonais', '<p>\n	Mayonais</p>\n', '<p>\n	Mayonais</p>\n', NULL, NULL, 1, NULL, 1, 1432742735, 1432742819),
(5, 'tbl_page_menus', 2, 1, NULL, 'Produk Kami', 'produk-kami', NULL, '<p>\n	Mulai dari mayonais, dressing, hingga olesan untuk roti, Kewpie Indonesia menghadirkan berbagai jenis produk yang &nbsp;menjadi pelezat hidangan Anda!&nbsp;</p>\n', NULL, NULL, 1, NULL, 1, 1432743088, 1432895755),
(6, 'tbl_page_menus', 3, 1, NULL, 'Di Dalam Kewpie', 'di-dalam-kewpie', '<h3>\n	Asal Mula Mayonais Kewpie</h3>\n<p class="p1">\n	Touichiro Nakashima (penemu KEWPIE mayonnaise) pertama kali mengenal mayonais pada tahun 1912, saat ia berkunjung ke Amerika Serikat untuk mempelajari makanan kaleng. Saat itu, dalam kesehariannya orang Amerika menyantap salad kentang dengan mayonais. Santapan yang lezat dan bergizi ini begitu berkesan bagi Nakashima sehingga ia bertekad untuk menyebarluaskan mayonais di Jepang.</p>\n<p class="p1">\n	Sekembalinya ke Jepang, Nakashima mulai mengembangkan mayonais bergizi tinggi, yang banyak mengandung kuning telur. Mayonais tersebut mulai dijual di Jepang pada tahun 1925 dengan merek Kewpie. Kewpie Mayonnaise merupakan mayonais produksi dalam negeri pertama di Jepang.</p>\n<p class="p1">\n	Pada perkembangannya sekarang, selain mayonais, Kewpie menyediakan produk-produk makanan yang berkualitas untuk segala usia di berbagai negara di dunia.&nbsp;</p>\n<h3>\n	Bahan Dasar Kewpie Mayonnaise</h3>\n<p class="p1">\n	Terbuat dari telur, cuka dan minyak sayur yang diproses dengan teknologi modern Kewpie, Mayonnaise Kewpie diproduksi melalui proses emulsi yang sangat baik. Proses emulsi ini menghasilkan partikel yang lebih kecil dan lebih rapat, dibanding produk mayonnaise lain. Sehingga Kewpie mayonnaise memiliki rasa yang lembut dan padat.</p>\n', '<h2 class="p1">\n	<strong>Asal Mula Mayonais Kewpie</strong></h2>\n<p class="p1">\n	Touichiro Nakashima (penemu KEWPIE mayonnaise) pertama kali mengenal mayonais pada tahun 1912, saat ia berkunjung ke Amerika Serikat untuk mempelajari makanan kaleng. Saat itu, dalam kesehariannya orang Amerika menyantap salad kentang dengan mayonais. Santapan yang lezat dan bergizi ini begitu berkesan bagi Nakashima sehingga ia bertekad untuk menyebarluaskan mayonais di Jepang.</p>\n<p class="p1">\n	Sekembalinya ke Jepang, Nakashima mulai mengembangkan mayonais bergizi tinggi, yang banyak mengandung kuning telur. Mayonais tersebut mulai dijual di Jepang pada tahun 1925 dengan merek Kewpie. Kewpie Mayonnaise merupakan mayonais produksi dalam negeri pertama di Jepang.</p>\n<p class="p1">\n	Pada perkembangannya sekarang, selain mayonais, Kewpie menyediakan produk-produk makanan yang berkualitas untuk segala usia di berbagai negara di dunia.&nbsp;</p>\n<p class="p1">\n	&nbsp;</p>\n<h2 class="p1">\n	<strong>Bahan Dasar Kewpie Mayonnaise</strong></h2>\n<p class="p1">\n	Terbuat dari telur, cuka dan minyak sayur yang diproses dengan teknologi modern Kewpie, Mayonnaise Kewpie diproduksi melalui proses emulsi yang sangat baik. Proses emulsi ini menghasilkan partikel yang lebih kecil dan lebih rapat, dibanding produk mayonnaise lain. Sehingga Kewpie mayonnaise memiliki rasa yang lembut dan padat.</p>\n<p class="p1">\n	&nbsp;</p>\n<h2 class="p1">\n	<strong>Proses Produksi</strong></h2>\n<p class="p1">\n	Semua varian produk dari Kewpie Indonesia dibuat dari bahan-bahan berkualitas, diproses dengan baik sehingga menghasilkan produk yang aman dikonsumsi. Dengan tube berstandar pangan, produk-produk Mayonais, Olesan Sandwich serta Saus Manis mudah digunakan dan dibawa. Sangat praktis untuk membuat lining pada makanan atau kreasi gambar pada roti.</p>\n', NULL, NULL, 1, NULL, 1, 1432743463, 1433834320),
(7, 'tbl_page_menus', 4, 1, NULL, 'Resep', 'resep', NULL, '<p>\n	Resep</p>\n', NULL, NULL, 1, NULL, 1, 1432743483, 1432756684),
(8, 'tbl_page_menus', 5, 1, NULL, 'Tentang Kewpie', 'tentang-kewpie', NULL, '<p class="p1">\n	PT. KEWPIE INDONESIA berdiri pada tahun 2013 dan memulai penjualan pertama khusus Food Service pada bulan Oktober 2014 . Dengan sertifikat halal dan NKV, Kewpie Indonesia menyediakan produk makanan halal, aman serta berkualitas untuk masyarakat Indonesia.&nbsp;</p>\n<p class="p1">\n	&nbsp;</p>\n<h2 class="p1">\n	<span style="font-size:16px;"><strong>Timeline Perusahaan</strong></span></h2>\n<p class="p1">\n	<strong>1919</strong></p>\n<p class="p1">\n	Shokuhin Kogyo Co., Ltd, perusahaan awal yang sekarang disebut KEWPIE Corporation berdiri di Otaki-cho, Nakano-ku, Tokyo dan mulai memproduksi serta mengolah berbagai macam produk makanan.</p>\n<p class="p1">\n	&nbsp;</p>\n<p class="p1">\n	<strong>1925</strong></p>\n<p class="p1">\n	Penjualan KEWPIE Mayonnaise dimulai. Mayonais Kewpie dijual dalam kemasan toples kaca dengan berat 100 gram dan 128 gram, serta merupakan mayonaiss dalam kemasan toples pertama di Jepang.</p>\n<p class="p1">\n	&nbsp;</p>\n<p class="p1">\n	<strong>1957</strong></p>\n<p class="p1">\n	Perusahaan berganti nama menjadi KEWPIE Corporation, sesuai dengan nama merek mayonais yang sudah terkenal.</p>\n<p class="p1">\n	&nbsp;</p>\n<p class="p1">\n	<strong>2013</strong></p>\n<p class="p1">\n	PT. KEWPIE INDONESIA berdiri.</p>\n<p class="p1">\n	&nbsp;</p>\n<p class="p1">\n	<strong>2014</strong></p>\n<p class="p1">\n	Produk-produk PT. KEWPIE INDONESIA mendapatkan sertifikasi Halal dan NKV, serta memulai penjualan untuk produk Food Service.&nbsp;</p>\n<p class="p1">\n	&nbsp;</p>\n<p class="p1">\n	<strong>2015</strong></p>\n<p class="p1">\n	Penjualan produk retail PT. KEWPIE INDONESIA dimulai.&nbsp;</p>\n<p class="p1">\n	&nbsp;</p>\n<h2 class="p1">\n	<span style="font-size:16px;"><strong>Sertifikasi Halal dan NKV</strong></span></h2>\n<p class="p1">\n	Seluruh produk Kewpie Indonesia bersertifikat halal resmi yang dikeluarkan oleh LPPOM MUI. Produk-produk yang berbahan dasar telur telah memiliki NKV resmi.&nbsp;</p>\n', NULL, NULL, 1, NULL, 1, 1432743493, 1432905599),
