(28, 'tbl_product_recipes', 1, 1, NULL, 'Gado Gado Baru', 'gado-gado-baru', '<p>\n	Gado Gado Baru</p>\n', '<ul>\n	<li>\n		Kentang</li>\n	<li>\n		Wortel</li>\n	<li>\n		Telur Rebus</li>\n	<li>\n		Kerupuk Udang</li>\n	<li>\n		Kol</li>\n	<li>\n		Tauge</li>\n	<li>\n		Lontong</li>\n	<li>\n		Kewpie Saus Siram dengan Wijen Sangrai</li>\n</ul>\n', '<p>\n	Lebih enak di hidangkan Panas-panas</p>\n', '<ol>\n	<li>\n		Rebus kol, tauge, telur, wortel serta kentang.</li>\n	<li>\n		Potong-potong lontong.</li>\n	<li>\n		Goreng kerupuk udang.</li>\n	<li>\n		Sajikan semua bahan-bahan di atas piring</li>\n	<li>\n		Tuang saus siram di atasnya.</li>\n</ol>\n', 1, NULL, 1, 1432809431, 1433350281),
(29, 'tbl_product_recipes', 2, 1, NULL, 'Seafood Salad', 'seafood-salad', '<p>\n	Seafood Salad</p>\n', '<ul>\n	<li>\n		Udang</li>\n	<li>\n		Cumi</li>\n	<li>\n		Salmon</li>\n	<li>\n		Selada</li>\n	<li>\n		Tomat Ceri</li>\n	<li>\n		Paprika Kuning</li>\n	<li>\n		Kewpie Saus Siram dengan Kecap Ala Jepang</li>\n</ul>\n', '<p>\n	Lebih nikmat di hidangkan Hangat-hangat</p>\n', '<ol>\n	<li>\n		Rebus udang dan cumi.</li>\n	<li>\n		Panggang cumi di atas wajan.</li>\n	<li>\n		Potong paprika dan tomat ceri sesuai selera.</li>\n	<li>\n		Taruh selada di atas piring, tata semua bahan di atas selada</li>\n	<li>\n		Tuangkan saus siram di atasnya.</li>\n</ol>\n', 1, NULL, 1, 1432814048, 1433350367),
(30, 'tbl_product_recipes', 3, 1, NULL, 'Buffalo Chicken Salad', 'buffalo-chicken-salad', '<p>\n	Buffalo Chicken Salad</p>\n', '<ul>\n	<li>\n		Sayap Ayam Goreng</li>\n	<li>\n		Tomat</li>\n	<li>\n		Selada</li>\n	<li>\n		Daun Bawang</li>\n	<li>\n		Jeruk Nipis</li>\n	<li>\n		Kewpie Saus Siram Tomat Pedas</li>\n</ul>\n', '<p>\n	Hidangkan hangat lebih lezat</p>\n', '<ol>\n	<li>\n		Tata selada di atas piring.</li>\n	<li>\n		Taruh ayam goreng di atasnya.</li>\n	<li>\n		Taruh jeruk nipis dan bawang merah sesuai selera</li>\n	<li>\n		Tuang saus siram di atasnya.</li>\n</ol>\n', 1, NULL, 1, 1432814051, 1433350515),
(31, 'tbl_product_recipes', 4, 1, NULL, 'Caesar Salad ala Amerika', 'caesar-salad-ala-amerika', '<p>\n	Caesar Salad ala Amerika</p>\n', '<ul>\n	<li>\n		Ayam Panggang (daging dada)</li>\n	<li>\n		Buah Zaitun (potong membentuk lingkaran)</li>\n	<li>\n		Crouton</li>\n	<li>\n		Romaine Lettuce</li>\n	<li>\n		Kewpie Saus Siram Caesar</li>\n</ul>\n', '<p>\n	Hidangkan hangat akan menjadi sempurna</p>\n', '<ol>\n	<li>\n		Panggang ayam.</li>\n	<li>\n		Potong buah zaitun membentuk lingkaran</li>\n	<li>\n		Sobek-sobek daun selada.</li>\n	<li>\n		Taruh ayam dan buah zaitun di atas selada</li>\n	<li>\n		Tuangkan saus siram di atasnya.</li>\n</ol>\n', 1, NULL, 1, 1432818645, 1433350598),
(32, 'tbl_product_recipes', 5, 1, NULL, 'Salad Rebusan', 'salad-rebusan', '<p>\n	Salad Rebusan</p>\n', '<ul>\n	<li>\n		Udang</li>\n	<li>\n		Telur</li>\n	<li>\n		Brokoli</li>\n	<li>\n		Wortel</li>\n	<li>\n		Kentang</li>\n	<li>\n		Kewpie Saus Siram Thousand Island</li>\n</ul>\n<p>\n	&nbsp;</p>\n', '<p>\n	Lebih lezat di hidangkan hangat</p>\n', '<ol>\n	<li>\n		Rebus udang, brokoli, wortel, kentang serta telur.</li>\n	<li>\n		Tata sesuai selera</li>\n	<li>\n		Tuang saus siram di atasnya.</li>\n</ol>\n', 1, NULL, 1, 1432821791, 1433350688),
