(1, 4, NULL, 'New Gado Gado', 'new-gado-gado', '<p class="bodyText_Type_Color" itemprop="description" style="font-size: 14px; line-height: 17px;">\n	If you&rsquo;re a fan of mayonnaise, this salad is a must-try recipe.<br />\n	But don&rsquo;t forget to use it with KEWPIE MAYONNAISE</p>\n<p class="bodyText_Type_Color" style="font-size: 14px; line-height: 17px;">\n	JAPANESE STYLE for that extra tasty, unique flavour.</p>\n', '<ul>\n	<li>\n		Potato</li>\n	<li>\n		Carrots</li>\n	<li>\n		Boiled Eggs</li>\n	<li>\n		Prawn Crackers</li>\n	<li>\n		Cabbage</li>\n	<li>\n		Bean sprouts</li>\n	<li>\n		Rice cake</li>\n	<li>\n		KEWPIE SALAD DRESSING ROASTED SESAME</li>\n</ul>\n', '0c7ae-preview-samall.jpg', NULL, NULL, 'a2f90-bahan-utama.jpg', '<p>\n	Better served while Hot</p>\n', NULL, 'no', NULL, '1', '12', '<ol>\n	<li>\n		Boil cabbage, bean sprouts, eggs, carrots and potatoes.</li>\n	<li>\n		Cut rice cake.</li>\n	<li>\n		Fry shrimp crackers.</li>\n	<li>\n		Serve all ingredients on a plate</li>\n	<li>\n		Pour with Kewpie Salad Dressing Roasted Sesame.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432737062, 1433396661),
(2, 5, NULL, 'Seafood Salad', 'seafood-salad', '<p>\n	Seafood Salad</p>\n', '<ul>\n	<li>\n		shrimp</li>\n	<li>\n		squid</li>\n	<li>\n		salmon</li>\n	<li>\n		lettuce</li>\n	<li>\n		Cherry tomatoes</li>\n	<li>\n		Yellow peppers</li>\n	<li>\n		Kewpie Salad Dressing Soy Sauce</li>\n</ul>\n', '66df3-preview-small.jpg', NULL, NULL, '0311a-bahan-utama.jpg', '<p>\n	Better served while hot</p>\n', NULL, 'no', NULL, '2', '18', '<ol>\n	<li>\n		Boil shrimp and squid.</li>\n	<li>\n		Grill squid on a griddle.</li>\n	<li>\n		Cut peppers and cherry tomatoes to garnish.</li>\n	<li>\n		Put lettuce on a plate, arrange all the ingredients on top of it</li>\n	<li>\n		Pour with Kewpie salad dressing.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432809110, 1433350326),
(3, 6, NULL, 'Buffalo Chicken Salad', 'buffalo-chicken-salad', '<p>\n	Buffalo Chicken Salad</p>\n', '<ul>\n	<li>\n		Fried chicken wing</li>\n	<li>\n		Tomato</li>\n	<li>\n		Lettuce</li>\n	<li>\n		Onion Leaves</li>\n	<li>\n		Lemonade</li>\n	<li>\n		Kewpie Salad Dressing Spicy Tomato&nbsp;</li>\n</ul>\n', '776d7-small-preview.jpg', NULL, NULL, '4d649-bahan-utama.jpg', '<p>\n	Better served hot</p>\n', NULL, 'no', NULL, '3', '19', '<ol>\n	<li>\n		Arrange lettuce on a plate.</li>\n	<li>\n		Place the fried chicken on it.</li>\n	<li>\n		Put lime juice and red onion to garnish</li>\n	<li>\n		Pour Kewpie Salad Dressing on it</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432809399, 1433350439),
(4, 7, NULL, 'American Caesar Salad', 'american-caesar-salad', '<p>\n	American Caesar Salad</p>\n', '<ul>\n	<li>\n		Grilled chicken (breast)</li>\n	<li>\n		Olive (cut round)</li>\n	<li>\n		Crouton</li>\n	<li>\n		Romaine Lettuce</li>\n	<li>\n		Kewpie Salad Dressing Caesar&nbsp;</li>\n</ul>\n', '2a0d6-preview-small.jpg', NULL, NULL, '83b83-bahan-utama.jpg', '<p>\n	Served hot will be perfect</p>\n', NULL, 'no', NULL, '3', '20', '<ol>\n	<li>\n		Roast chicken.</li>\n	<li>\n		Cut olives in round shape</li>\n	<li>\n		Rip lettuce.</li>\n	<li>\n		Place the chicken and olives on top the lettuce.</li>\n	<li>\n		Pour Kewpie salad dressing on it.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432818546, 1433350555),
(5, 8, NULL, 'Boiled Salad', 'boiled-salad', '<p>\n	Boiled Salad</p>\n', '<ul>\n	<li>\n		shrimp</li>\n	<li>\n		egg</li>\n	<li>\n		broccoli</li>\n	<li>\n		carrots</li>\n	<li>\n		Potato</li>\n	<li>\n		Kewpie Salad Dressing Thousand Islands</li>\n</ul>\n', 'cd851-preview-small.jpg', NULL, NULL, 'd8e89-bahan-utama.jpg', '<p>\n	Better served while hot</p>\n', NULL, 'no', NULL, '5', '25', '<ol>\n	<li>\n		Boiled shrimp, broccoli, carrots, potatoes and eggs.</li>\n	<li>\n		Arrange on a plate</li>\n	<li>\n		Pour salad dressing over the arrangement.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432821748, 1433350642),
(6, 10, NULL, 'Hot Dog', 'hot-dog', '<p>\n	Hot Dog</p>\n', '<ul>\n	<li>\n		hotdog bun</li>\n	<li>\n		chicken sausage</li>\n	<li>\n		Pickles</li>\n	<li>\n		lettuce</li>\n	<li>\n		chili sauce</li>\n	<li>\n		Kewpie Sandwich Spread Cheese</li>\n</ul>\n', NULL, NULL, NULL, NULL, '<p>\n	Better served while Hot</p>\n', NULL, 'no', NULL, '5', '35', '<ol>\n	<li>\n		Place the lettuce in hotdog bun.</li>\n	<li>\n		Add pickles.</li>\n	<li>\n		Place chicken sausage.</li>\n	<li>\n		Add chili sauce (if desired) and coat sausage with sandwich spread on it.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432829829, 1433350726),
(7, 1, NULL, 'Fried Chicken with Mayo Dip', 'fried-chicken-with-mayo-dip', '<p>\n	Fried Chicken with Mayo Dip</p>\n', '<ul>\n	<li>\n		Fried Chicken</li>\n	<li>\n		carrots</li>\n	<li>\n		lettuce</li>\n	<li>\n		broccoli</li>\n	<li>\n		Baby potatoes</li>\n	<li>\n		Lime</li>\n	<li>\n		Kewpie Mayonnaise Original</li>\n</ul>\n<p>\n	&nbsp;</p>\n', '574f8-preview-small.jpg', NULL, NULL, 'da97e-bahan-utama.jpg', '<p>\n	Better served while HOT</p>\n', NULL, '', NULL, '3', '15', '<ol>\n	<li>\n		Boil carrots, broccoli and potatoes.</li>\n	<li>\n		Arrange lettuce on a plate.</li>\n	<li>\n		Serve carrots, broccoli, potatoes and fried chicken on a plate.</li>\n	<li>\n		Add mayonnaise as condiment</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432911778, 1433350798),
(8, 2, NULL, 'Easy Mayo Dips', 'easy-mayo-dips', '<p>\n	Easy Mayo Dips</p>\n', '<ul>\n	<li>\n		Fritters</li>\n	<li>\n		Fried cassava</li>\n	<li>\n		Fried Tempe</li>\n	<li>\n		Kewpie Mayonnaise Original Sweet&nbsp;</li>\n</ul>\n<p>\n	&nbsp;</p>\n', NULL, NULL, NULL, NULL, '<p>\n	Better served while hot</p>\n', NULL, '', NULL, '2', '15', '<p>\n	Serve fritter, fried cassava and fried tempeh with sweet mayonnaise.</p>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432911889, 1433350885),
(9, 3, NULL, 'Ebi Fry', 'ebi-fry', '<p>\n	Ebi Fry</p>\n', '<ul>\n	<li>\n		Ebi Fry</li>\n	<li>\n		cucumber</li>\n	<li>\n		tomatoes</li>\n	<li>\n		Sweet soy sauce (optional)</li>\n	<li>\n		lime</li>\n	<li>\n		Cabbage</li>\n	<li>\n		KEWPIE MAYONNAISE SAVORY</li>\n</ul>\n', NULL, NULL, NULL, NULL, NULL, NULL, '', NULL, '5', '15', '<ol>\n	<li>\n		Thinly slice cabbage.</li>\n	<li>\n		Put ebi fry over cabbage.</li>\n	<li>\n		Cut tomato, cucumber and lime, decorate all on the plate.</li>\n	<li>\n		Add soy sauce and Kewpie Mayo Savory</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432911997, 1433350923),
(10, 1, NULL, 'Egg Sandwich', 'egg-sandwich', '<p>\n	Egg Sandwich</p>\n', '<ul>\n	<li>\n		3 Slices Crustless Bread</li>\n	<li>\n		2 Boiled Eggs</li>\n	<li>\n		Salt to taste</li>\n	<li>\n		2 tbs Kewpie mayonnaise Original</li>\n</ul>\n', 'cb1c9-preview.jpg', NULL, NULL, NULL, '<p>\n	Well served while hot</p>\n', NULL, 'no', NULL, '2', '23', '<ol>\n	<li>\n		Put boiled eggs into a bowl. Mash using fork until they become crumbles. Add salt and mayonnaise, mix until blended.</li>\n	<li>\n		Prepare the bread, fill with mayonnaise as you like it, add boiled egg mixtures. Repeat to all breads, then cut them into triangles. Sandwiches are ready to be served.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432912861, 1433351099),
(11, 9, NULL, 'Rolled Tuna Sandwich', 'rolled-tuna-sandwich', NULL, '<ul>\n	<li>\n		4 slices white bread</li>\n	<li>\n		Canned tuna&nbsp;</li>\n	<li>\n		1 lettuce</li>\n	<li>\n		&frac12; sheet of Nori/Dried Seaweed</li>\n	<li>\n		KEWPIE SANDWICH SPREAD ORIGINAL</li>\n</ul>\n', '2bb87-food-kewpie_tumisayamwijen_wbg.jpg', '<iframe width="560" height="315" src="https://www.youtube.com/embed/zc8IR7srgdM" frameborder="0" allowfullscreen></iframe>', NULL, 'b8d63-olesan-sandwich.jpg', '<p>\n	Its unique shape, ideal for a picnic or snacks in a party</p>\n', NULL, '', NULL, '4', '10', '<div>\n	<ol>\n		<li>\n			Cut nori slightly smaller than the size of the bread.</li>\n		<li>\n			Apply KEWPIE SANDWICH ORIGINAL spread on the bread.</li>\n		<li>\n			Put lettuce, nori, and tuna then rolled. Cut the roll into two pieces.</li>\n	</ol>\n	<div>\n		&nbsp;</div>\n</div>\n<div>\n	*) Before filling the bread, you can flatten the bread with bread roller.</div>\n<div>\n	**) You can also use sushi mat to help rolling the bread.</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', 1432913145, 1433351041),
(14, 1, NULL, 'Potato Salad', 'potato-salad', NULL, '<ul>\n	<li>\n		2 Potatoes</li>\n	<li>\n		&frac14; onion</li>\n	<li>\n		&frac14; carrot</li>\n	<li>\n		&frac14; cucumber</li>\n	<li>\n		2 sheets Smoked Beef&nbsp;</li>\n	<li>\n		Salt to taste</li>\n	<li>\n		Pepper to taste</li>\n	<li>\n		5 tbsp KEWPIE MAYONNAISE ORIGINAL</li>\n</ul>\n', 'd5a30-potato-salad-1920.jpg', '<iframe width="560" height="315" src="https://www.youtube.com/embed/HfALA3jMxj0" frameborder="0" allowfullscreen></iframe>', NULL, '65f37-mayo-original.jpg', '<p>\n	The tasty combination of potatoes and mayonnaise allow you to add other ingredients when creating your potato salad</p>\n', NULL, 'no', NULL, '2', '15', '<ol>\n	<li>\n		Wash potatoes, wrap while still wet with kitchen paper, and wrap again with cling wrap.&nbsp;</li>\n	<li>\n		Heat in microwave (800W) for about 6 minutes, then peel the skin and mash with a fork while they are still hot.</li>\n	<li>\n		Cut onion in thin slice. Peel carrots and cut into &frac14; circle. Put the onion and carrot in heat resistant plate then cover with plastic wrap.&nbsp;</li>\n	<li>\n		Heat in microwave oven (800 W) for 2 minutes.</li>\n	<li>\n		Cut cucumber and smoked beef in thin slice.</li>\n	<li>\n		Mix all ingredients with the mashed potato.</li>\n	<li>\n		Add KEWPIE MAYONNAISE ORIGINAL when potato is already cold, add salt and pepper after.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	*) As an alternative, potatoes can also be wrapped in aluminum foil and steamed for 20 minutes.</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(12, 1, NULL, 'Egg Salad On Sandwich', 'egg-salad-on-sandwich', '<p>\n	Egg Salad on Sandwich</p>\n', '<ul>\n	<li>\n		2 slices white bread</li>\n	<li>\n		2 lettuces</li>\n	<li>\n		2 eggs</li>\n	<li>\n		1 Beef Bacon</li>\n	<li>\n		cooking oil</li>\n	<li>\n		Kewpie mayonnaise original</li>\n	<li>\n		*Bacon can be replaced with smoked beef</li>\n</ul>\n', '58e7c-preview-small.jpg', '<iframe width="854" height="510" src="https://www.youtube.com/embed/g8QZX9o9qzw" frameborder="0" allowfullscreen></iframe>', NULL, 'e4111-bahan-utama.jpg', '<p>\n	Well served while Hot</p>\n', NULL, '', NULL, '2', '35', '<ol>\n	<li>\n		Wash the lettuce with cold water, then drain. Rip to an edible size.</li>\n	<li>\n		Cut the bacon approximately 2 cm, sauteed in the pan without oil. Add oil in the same skillet, wait until hot, put beaten egg and stir till cooked.</li>\n	<li>\n		Put lettuce, bacon and egg at the top of the bread, add mayonnaise on it.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432913333, 1433351165),
(13, 1, NULL, 'Broccoli Sandwich & Sauteed Mayo Eggs', 'broccoli-sandwich-sauteed-mayo-eggs', '<p>\n	Broccoli Sandwich &amp; Sauteed Mayo Eggs</p>\n', '<ul>\n	<li>\n		6 slices Bread</li>\n	<li>\n		&frac12; head Broccoli</li>\n	<li>\n		2 slices Beef Bacon</li>\n	<li>\n		2 eggs</li>\n	<li>\n		pepper to taste</li>\n	<li>\n		&frac12; tbsp KEWPIE MAYONNAISE ORIGINAL</li>\n</ul>\n', '4bd16-preview-small.jpg', NULL, NULL, '712fe-bahan-utama.jpg', '<div>\n	<span style="font-size: 12px;">A Healthy menu ideal for party or a gathering event. Will be loved by children who have problems with eating vegetables</span></div>\n', NULL, '', NULL, '2', '23', '<ol>\n	<li>\n		6 slices Bread</li>\n	<li>\n		&frac12; head Broccoli</li>\n	<li>\n		2 slices Beef Bacon</li>\n	<li>\n		2 eggs</li>\n	<li>\n		pepper to taste</li>\n	<li>\n		&frac12; tbsp&nbsp;<span style="font-size: 12.0120115280151px;">KEWPIE MAYONNAISE ORIGINAL</span></li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'unpublish', 1432913459, 1433351247),
(15, 2, NULL, 'Fruit Salad', 'fruit-salad', NULL, '<ul>\n	<li>\n		100 grams watermelon, cubed</li>\n	<li>\n		100 grams melon, diced</li>\n	<li>\n		1 apple, diced</li>\n	<li>\n		100 grams lettuce, torn</li>\n	<li>\n		&frac12; Dragon Fruit, cubed</li>\n	<li>\n		100 grams cherry tomatoes, halved</li>\n	<li>\n		6 pieces strawberries, halved</li>\n	<li>\n		6 canned citrus&nbsp;</li>\n	<li>\n		KEWPIE MAYONNAISE ORIGINAL SWEET to taste</li>\n</ul>\n', 'c987c-food-kewpie_tumisayamwijen_3.jpg', NULL, NULL, '608e8-mayo-manis.jpg', '<p>\n	KEWPIE MAYONNAISE ORIGINAL SWEET gives a sweet and creamy taset when mixed with sweet fruit. Create your own fruit salad with your favorite fruits.</p>\n', NULL, '', NULL, '2', '10', '<ol>\n	<li>\n		Mix all the ingredients in a bowl, add KEWPIE MAYONNAISE ORIGINAL SWEET as you like.&nbsp;</li>\n	<li>\n		Create lining using mayonnaise to make it looks more attractive.</li>\n	<li>\n		Mix mayonnaise and fruit well before serving.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(16, 3, NULL, 'Tuna and Corn Savory Onigiri', 'tuna-and-corn-savory-onigiri', NULL, '<ul>\n	<li>\n		400 g rice</li>\n	<li>\n		Canned tuna</li>\n	<li>\n		1 sheet Smoked Beef</li>\n	<li>\n		2 tablespoons Corn</li>\n	<li>\n		2 tsp KEWPIE MAYONNAISE SAVORY SAUCE</li>\n</ul>\n', '34ddf-food-kewpie_tumisayamwijen_3.jpg', NULL, NULL, '6194c-mayo-gurih.jpg', '<p>\n	Mayonnaise makes onigiri or rice balls more tasty. This menu is &nbsp;great as snack or in your lunchbox1</p>\n', NULL, '', NULL, '3', '10', '<div>\n	<strong>Tuna Onigiri</strong></div>\n<ol>\n	<li>\n		Mix rice and canned tuna with mayo sauce in a bowl. Shape into onigiri.</li>\n</ol>\n<div>\n	<strong>Corn Onigiri&nbsp;</strong></div>\n<ol>\n	<li>\n		Cut smoked beef squared by 1 cm.</li>\n	<li>\n		Mix rice, smoked beef, corn and KEWPIE MAYONNAISE SAVORY SAUCE in one bowl. Shape into onigiri.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(17, 4, NULL, 'Stir-fried Roasted Sesame Chicken', 'stir-fried-roasted-sesame-chicken', NULL, '<ul>\n	<li>\n		2 pieces Chicken Thighs</li>\n	<li>\n		KEWPIE Salad Dressing Roasted Sesame to taste</li>\n	<li>\n		Salt to taste</li>\n</ul>\n<div>\n	<strong>Ingredients for Dressing:</strong></div>\n<ul>\n	<li>\n		1/8 Onions</li>\n	<li>\n		&frac14; cucumber</li>\n	<li>\n		10g Red Peppers</li>\n	<li>\n		1 egg</li>\n	<li>\n		KEWPIE Salad Dressing Roasted Sesame to taste</li>\n</ul>\n', 'bb7a4-food-kewpie_tumisayamwijen_flip.jpg', NULL, NULL, '95ee9-wijen-sangrai.jpg', '<p>\n	KEWPIE Salad Dressing Roasted Sesame is very tasty for any variety of chicken dishes</p>\n', NULL, 'no', NULL, '2', '15', '<div>\n	&nbsp;</div>\n<div>\n	<strong>Sauce :</strong></div>\n<ol>\n	<li>\n		Chop all vegetables small dice, mix with eggs in a bowl and stir well.</li>\n	<li>\n		Heat a frying pan, cook the egg mixture until slightly congested, then remove.</li>\n	<li>\n		While hot, mix it with KEWPIE Salad Dressing Roasted Sesame and stir until egg turns into scramble.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	<strong>Chicken:</strong></div>\n<ol>\n	<li>\n		Marinate chicken with KEWPIE Salad Dressing Roasted Sesame and salt for 1 hour.</li>\n	<li>\n		After it&#39;s permeated well, heat a frying pan and then stir-fry the chicken until golden brown. Remove and set aside.</li>\n	<li>\n		Cut chicken, arrange on a plate, put the sauce ingredients on it. You can add more KEWPIE Salad Dressing Roasted Sesame if you like.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(19, 10, NULL, 'Scrambled Eggs Sandwich', 'scrambled-eggs-sandwich', NULL, '<ul>\n	<li>\n		2 Soft rolls or Dinner rolls), cut it two in the middle</li>\n	<li>\n		4 slices of cucumber, cut on bias</li>\n	<li>\n		2 Eggs</li>\n	<li>\n		1 tsp Margarine</li>\n	<li>\n		2 tsp KEWPIE MAYONNAISE ORIGINAL</li>\n	<li>\n		2 tsp KEWPIE SANDWICH SPREAD CHEESE</li>\n</ul>\n', 'da65a-food-kewpie_sandwichtelur_3.jpg', '<iframe width="480" height="270" src="https://www.youtube.com/embed/S-g6xVD4AVM" frameborder="0" allowfullscreen></iframe>', NULL, 'c4f8f-sandwich-telur.jpg', '<p>\n	The mix of KEWPIE MAYONNAISE ORIGINAL in scrambled eggs makes it softer even when it&rsquo;s cold. While KEWPIE SANDWICH SPREAD ORIGINAL CHEESE enrich the taste of your sandwich.</p>\n', NULL, '', NULL, '2', '10', '<ol>\n	<li>\n		Add the eggs and KEWPIE MAYONNAISE ORIGINAL into a bowl, beat until evenly mixed.</li>\n	<li>\n		Heat margarine in a frying pan, put the eggs, make a scramble*.Remove from heat and set aside.</li>\n	<li>\n		Spread KEWPIE SANDWICH SPREAD CHEESE on the inside of the bread, then fill with scrambled egg. Add cucumber, serve.</li>\n</ol>\n<p>\n	*) As an alternative way of cooking, scrambled egg can also be made by beating eggs and mayonnaise in a bowl, then cover with cling wrap. Heat using a microwave with 800w power for 1 minute.</p>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(20, 2, NULL, 'Apple Caramel', 'apple-caramel', NULL, '<ul>\n	<li>\n		1 apple</li>\n	<li>\n		2 tsp KEWPIE MAYONNAISE ORIGINAL SWEET</li>\n	<li>\n		2 tablespoons Sugar</li>\n	<li>\n		5 tablespoons water</li>\n	<li>\n		Vanilla ice cream to taste</li>\n</ul>\n', '74999-apel-karamel-5.jpg', NULL, NULL, '59b04-mayo-manis.jpg', '<p>\n	KEWPIE MAYONNAISE ORIGINAL SWEET gives fruit a softer texture and add sweetness.</p>\n', NULL, '', NULL, '2', '15', '<ol>\n	<li>\n		Peel apple, cored, cut into slices lengthwise.</li>\n	<li>\n		Add KEWPIE MAYONNAISE ORIGINAL SWEET into skillet, light the fire.&nbsp;</li>\n	<li>\n		Baked apple with a small flame until it changes color.</li>\n	<li>\n		Once the apple become soft, add water and sugar. Cook until the color changed into the color of caramel, mix evenly.</li>\n	<li>\n		Remove and serve on a plate. While hot, add the vanilla ice cream.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(22, 1, NULL, 'Date Salad', 'date-salad', NULL, '<ul>\n	<li>\n		6 Dates</li>\n	<li>\n		Lettuce</li>\n	<li>\n		1 pack Salad Mix</li>\n	<li>\n		2 Cherry Tomatoes</li>\n	<li>\n		Pineapple slices</li>\n	<li>\n		KEWPIE MAYONNAISE ORIGINAL to taste</li>\n</ul>\n', '73a89-food-kewpie_tumisayamwijen_3.jpg', NULL, NULL, 'e3ec2-mayo-original.jpg', '<div>\n	KEWPIE MAYONNAISE ORIGINAL makes vegetables taste better. Enrich your salad by adding more favorite fruits!</div>\n<div>\n	&nbsp;</div>\n', NULL, '', NULL, '2', '5', '<ol>\n	<li>\n		Torn lettuce to small size, arrange on a plate.</li>\n	<li>\n		Add dates, salad mix, cherry tomatoes and pineapple.</li>\n	<li>\n		Add mayonnaise over it.&nbsp;</li>\n	<li>\n		Stir before serving.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(23, 4, NULL, 'Sauteed Water spinach and Shrimp with Roasted Sesame', 'sauteed-water-spinach-and-shrimp-with-roasted-sesame', NULL, '<ul>\n	<li>\n		3 unches Water pinach</li>\n	<li>\n		&frac12; Onion</li>\n	<li>\n		2 cloves Garlic</li>\n	<li>\n		6 medium size shrimps, peeled</li>\n	<li>\n		1 large red chili</li>\n	<li>\n		Cooking Oil</li>\n	<li>\n		KEWPIE Salad Dressing Roasted Sesame to taste</li>\n</ul>\n', 'b7993-tumis-kangkung-2.jpg', NULL, NULL, '33771-wijen-sangrai.jpg', '<div>\n	Not only taste best with salad, KEWPIE Salad Dressing with Roasted Sesame also gives a distinctive flavor to the stir fry dishes.</div>\n<div>\n	With a favorite flavor, bring a new experience to enjoy vegetables!</div>\n', NULL, '', NULL, '4', '15', '<ol>\n	<li>\n		Wash water spinach and pick.</li>\n	<li>\n		Thinly slice onions and peppers.&nbsp;</li>\n	<li>\n		Chopped garlic.</li>\n	<li>\n		Heat oil, saute onion, garlic, chilli pepper, and add shrimps.</li>\n	<li>\n		Add spinach and cook until shriveled.</li>\n	<li>\n		Serve on a plate, pour KEWPIE Salad Dressing Roasted Sesame on it.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(24, 5, NULL, 'Sauteed Water Spinach and Seafood with Japanese Soy Sauce', 'sauteed-water-spinach-and-seafood-with-japanese-soy-sauce', NULL, '<ul>\n	<li>\n		3 bunches water spinach</li>\n	<li>\n		&frac12; Onion</li>\n	<li>\n		2 cloves Garlic</li>\n	<li>\n		2 squids, sliced &frac12; cm</li>\n	<li>\n		6 medium size shrimps, peeled, leave the tail</li>\n	<li>\n		4 Fish Meatballs, halved</li>\n	<li>\n		Cooking Oil&nbsp;</li>\n	<li>\n		KEWPIE Salad Dressing with Japanese Soy Sauce to taste</li>\n</ul>\n<div>\n	&nbsp;</div>\n', 'af132-tumis-kangkung-kecap-jepang.jpg', '<iframe width="560" height="315" src="https://www.youtube.com/embed/A_zN3zVo8YQ" frameborder="0" allowfullscreen></iframe>', NULL, '4ed3a-kecap-ala-jepang.jpg', '<p>\n	KEWPIE Salad Dressing with Japanese Soy Sauce is great for stir fry dishes. Simply add and make your ordinary food becomes more delicious.</p>\n', NULL, '', NULL, '4', '15', '<ol>\n	<li>\n		Wash water spinach then pick.</li>\n	<li>\n		Thinly slice onion, mince garlic.</li>\n	<li>\n		Heat oil and saute onion and garlic until cooked, add squid.</li>\n	<li>\n		Add water spinach and cook until shriveled.</li>\n	<li>\n		Add KEWPIE Salad Dressing with Japanese Soy sauce, stir until blended. Serve while hot.</li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(25, 5, NULL, 'Pineapple Salad with KEWPIE Salad Dressing Soy Sauce', 'pineapple-salad-with-kewpie-salad-dressing-soy-sauce', NULL, '<ul>\n	<li>\n		&frac12; Pineapple</li>\n	<li>\n		3 Leaves of Lettuce</li>\n	<li>\n		&frac12; pack Packaged Salad Mix</li>\n	<li>\n		&frac14; watermelon, cubed</li>\n	<li>\n		&frac14; Papaya, cubed</li>\n	<li>\n		2 red cayenne pepper, sliced</li>\n	<li>\n		KEWPIE Salad Dressing with Japanese Soy Sauce to taste</li>\n</ul>\n<div>\n	&nbsp;</div>\n', '08edf-salad-nanas-3.jpg', NULL, NULL, '320d4-kecap-ala-jepang.jpg', '<div>\n	Enjoy delicious fruit salad in a new style!</div>\n<div>\n	Not only delicious for stir fried dishes, KEWPIE Salad Dressing with Japanese Soy Sauce is also super tasty as fruit salad dressing.</div>\n<div>\n	If spicy is not your thing, just put the cayenne aside.&nbsp;</div>\n', NULL, 'no', NULL, '4', '10', '<div>\n	<em>Sauce</em></div>\n<ol>\n	<li>\n		Add KEWPIE Salad Dressing with Japanese Soy Sauce.</li>\n	<li>\n		add sliced cayenne</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	<em>Salad</em></div>\n<ol>\n	<li>\n		Arrange lettuce and mixed salad on a plate.</li>\n	<li>\n		Add all the fruits and then pour the sauce over it.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(26, 6, NULL, 'Vegetable Pasta with Spicy Tomato Sauce', 'vegetable-pasta-with-spicy-tomato-sauce', NULL, '<ul>\n	<li>\n		150 g Fettucini&nbsp;</li>\n	<li>\n		3 Garlic cloves, roughly chopped&nbsp;</li>\n	<li>\n		&frac12; Onion, roughly chopped&nbsp;</li>\n	<li>\n		2 sheets Smoked Beef, cut lengthwise</li>\n	<li>\n		100 g Frozen Mixed Vegetables</li>\n	<li>\n		100 g broccoli, cut into small florets</li>\n	<li>\n		1000 ml Water&nbsp;</li>\n	<li>\n		100 ml KEWPIE Salad Dressing Spicy Tomato</li>\n</ul>\n', '3f228-pasta-sayuran1.jpg', NULL, NULL, '4b029-tomat-pedas.jpg', '<div>\n	Use any kind of pasta you like. Add cheddar or parmesan cheese as toppings to add more taste.</div>\n<div>\n	Kewpie Salad Dressing Spicy Tomato is a ready to use pasta sauce.</div>\n', NULL, '', NULL, '2', '20', '<ol>\n	<li>\n		Boil water, 1/2 teaspoon salt, and 1 tablespoon of cooking oil.</li>\n	<li>\n		Add fettucini. Boil until cooked, remove and drain.</li>\n	<li>\n		Saut&eacute; garlic and onion until cooked and fragrant.</li>\n	<li>\n		Add bacon, broccoli and frozen mixed vegetables, stir well.</li>\n	<li>\n		Add fettucini and mix together.</li>\n	<li>\n		Add KEWPIE Salad Dressing Spicy Tomato, stir well. Remove and serve.</li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(28, 7, NULL, 'Ifumie with Caesar Sauce', 'ifumie-with-caesar-sauce', NULL, '<ul>\n	<li>\n		200 g egg noodles</li>\n	<li>\n		1 piece Onion, sliced thick and long</li>\n	<li>\n		3 Garlic cloves, minced</li>\n	<li>\n		75 g shrimp, peeled leave tails</li>\n	<li>\n		1 Carrot, cut on the bias</li>\n	<li>\n		100 g broccoli, cut into small florets</li>\n	<li>\n		100 g mushroom, halved</li>\n	<li>\n		200 ml water</li>\n	<li>\n		2 leeks, cut on the bias</li>\n	<li>\n		1 egg</li>\n	<li>\n		2 tablespoons of cooking oil, for frying</li>\n	<li>\n		100 ml KEWPIE Caesar Salad Dressing</li>\n</ul>\n', '82e68-ifumie.jpg', NULL, NULL, '637e0-caesar.jpg', '<p>\n	KEWPIE Caesar Salad Dressing gives a unique blend of eastern and western food, bring a new flavor to pamper your tastebud.</p>\n', NULL, 'no', NULL, '2', '25', '<ol>\n	<li>\n		Boil noodles until half cooked, remove from heat.</li>\n	<li>\n		Put the noodles into mesh skimmer, deep-fried noodle until crispy. Remove from heat, set aside.</li>\n	<li>\n		Heat oil in a frying pan, put onion and garlic stir-fry until fragrant.</li>\n	<li>\n		Add shrimp, and cook until it changes color. Add vegetables and KEWPIE Caesar Salad Dressing, stir well.</li>\n	<li>\n		Pour water and cook until boiling. Add the eggs, stir until stringy.</li>\n	<li>\n		Add leeks, Stir, and pour onto ifumie.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(29, 8, NULL, 'Broccoli Chicken Nugget with Thousand Island Sauce', 'broccoli-chicken-nugget-with-thousand-island-sauce', NULL, '<ul>\n	<li>\n		200 g &nbsp;Broccoli, steamed and well blended.</li>\n	<li>\n		250 g minced chicken&nbsp;</li>\n	<li>\n		50 g soft breadcrumbs&nbsp;</li>\n	<li>\n		2 eggs, beaten</li>\n	<li>\n		&frac34; tsp Salt &nbsp;</li>\n	<li>\n		&frac12; tsp Pepper</li>\n</ul>\n<p>\n	Dipping Ingredients:</p>\n<ul>\n	<li>\n		&frac14; tsp Refined Sugar</li>\n	<li>\n		2 Egg white&nbsp;</li>\n	<li>\n		1 tsp garlic powder</li>\n	<li>\n		150 g dry yellow breadcrumbs</li>\n	<li>\n		50 g Cheddar Cheese, grated</li>\n	<li>\n		KEWPIE Salad Dressing Thousand Island to taste</li>\n</ul>\n', '4aad9-food-kewpie_nuggetayam_3.jpg', NULL, NULL, 'd8426-thousand-island.jpg', '<p>\n	A Healthy menu ideal for party or a gathering event. Will be loved by children who have problems with eating vegetables</p>\n', NULL, 'no', NULL, '20', '40', '<ol>\n	<li>\n		Mix and stir all ingredients</li>\n	<li>\n		Pour in a rectangular baking dish (size 22x22x3 cm) spread with oil and covered with plastic.</li>\n	<li>\n		Steam for 30 minutes over medium heat until cooked. Allow to cool.</li>\n	<li>\n		Cut into pieces approximately 1.5x5 cm.</li>\n	<li>\n		Dip into dipping sauce. Roll in breadcrumbs.*</li>\n	<li>\n		Fried in medium heat cooking oil until well cooked.</li>\n	<li>\n		Serve with Thousand Island dressing</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	*) Soft breadcrumbs can be replaced with dry breadcrumbs</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(30, 7, NULL, 'Sliced Potato with Caesar', 'sliced-potato-with-caesar', NULL, '<ul>\n	<li>\n		3 Potato&nbsp;</li>\n	<li>\n		10 Pepperoni, minced</li>\n	<li>\n		50 g Mozzarella Cheese&nbsp;</li>\n	<li>\n		100 ml KEWPIE Salad Dressing Caesar</li>\n</ul>\n', 'aa31b-food-kewpie_kentangiris_3.jpg', NULL, NULL, '9fdb7-caesar.jpg', '<p>\n	The menu is perfect for a party or a gathering event. You can replace the mozzarella cheese with any kind of melting cheese.</p>\n', NULL, 'no', NULL, '3', '35', '<ol>\n	<li>\n		Thin slice potato into pieces but not all the way.</li>\n	<li>\n		Preheat oven into 175 celcius degree.</li>\n	<li>\n		Pour KEWPIE Salad Dressing Caesar over the potato and bake for approximately 30 minutes or until cooked well.&nbsp;</li>\n	<li>\n		Take out potato, sprinkle with Mozarella cheese and pepperoni. Bake again for 5 minutes until all the cheese are melted.&nbsp;</li>\n	<li>\n		Remove from oven, sprinkle with onion leaves. Serve.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	*) You can also enjoy this dish by dipping sliced potato into KEWPIE Salad Dressing Caesar.</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(31, 9, NULL, 'Tomato and Egg Sandwich', 'tomato-and-egg-sandwich', NULL, '<ul>\n	<li>\n		4 slices White Bread</li>\n	<li>\n		1 egg, boiled</li>\n	<li>\n		2 cheese singles</li>\n	<li>\n		&frac12; tomato</li>\n	<li>\n		1 Lettuce</li>\n	<li>\n		KEWPIE SANDWICH SPREAD ORIGINAL to taste</li>\n</ul>\n', 'a91b7-sandwich-tomat-telur-3.jpg', '<iframe width="560" height="315" src="https://www.youtube.com/embed/TQ4EBgYYudI" frameborder="0" allowfullscreen></iframe>', NULL, '56ce5-olesan-sandwich.jpg', '<p>\n	With KEWPIE SANDWICH SPREAD ORIGINAL, you can make your own healthy and delicious sandwichat home. Yummy for breakfast and for lunch.</p>\n', NULL, '', NULL, '2', '5', '<ol>\n	<li>\n		Thin slice egg and tomato.</li>\n	<li>\n		Wash lettuce and drain.&nbsp;</li>\n	<li>\n		Tear lettuce into smaller size.</li>\n	<li>\n		Apply KEWPIE SANDWICH SPREAD ORIGINAL on one side of each bread.</li>\n	<li>\n		Put lettuce, tomato, cheese and boiled eggs on bread, then put another bread on top of the pile.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	<div>\n		*) Wrap sandwich with plastic wrap, press with a plate to put some weight over it for 10 minutes</div>\n	<div>\n		&nbsp;This will make sandwich to stay together.</div>\n</div>\n<p>\n	&nbsp;</p>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(32, 13, NULL, 'Drawing on The Bread', 'drawing-on-the-bread', NULL, '<ul>\n	<li>\n		Slices of white bread</li>\n	<li>\n		KEWPIE SPREADS (All chocolate and vanilla variants)</li>\n</ul>\n', 'ae734-menggambar-di-atas-roti.jpg', '<iframe width="560" height="315" src="https://www.youtube.com/embed/hH0dr7psn3M" frameborder="0" allowfullscreen></iframe>', NULL, '2e87a-menggambar-diatas-roti.jpg', '<p>\n	Spread in a tube makes it easy to create any art on breads. Write your message or draw any faces and eat up.</p>\n', NULL, 'no', NULL, NULL, '5', '<ol>\n	<li>\n		Pick a bread, draw or write a message on top of the bread as you like.</li>\n	<li>\n		(Use any bread, and draw any message or picture you like.)</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(33, 16, NULL, 'Rainbow Banana with Sweet Spreads', 'rainbow-banana-with-sweet-spreads', NULL, '<ul>\n	<li>\n		6 Plantains</li>\n	<li>\n		6 Satay Skewers</li>\n	<li>\n		Sprinkles</li>\n	<li>\n		KEWPIE Spreads (All Chocolate and Vanilla Variants)</li>\n</ul>\n', '43eb9-pisang-pelangi3.jpg', NULL, NULL, 'e1ffb-menggambar-diatas-roti.jpg', '<p>\n	<span style="font-size: 11.9999990463257px;">Bananas will taste and look more wonderful with KEWPIE Spreads, especially for family and children.</span></p>\n', NULL, 'no', NULL, '6', '5', '<ol>\n	<li>\n		Peel bananas and thread it onto satay skewers.</li>\n	<li>\n		Apply KEWPIE Spreads (All Chocolate and Vanilla Flavor) to bananas. Add sprinkles on it too.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(34, 13, NULL, 'Mini Sweet Martabak', 'mini-sweet-martabak', NULL, '<ul>\n	<li>\n		225 g Medium Protein Flour</li>\n	<li>\n		75 g Sugar</li>\n	<li>\n		&frac14; tsp Baking Powder</li>\n	<li>\n		&frac14; tsp salt</li>\n	<li>\n		250 ml water</li>\n	<li>\n		1 egg</li>\n	<li>\n		&frac14; tsp Baking Soda</li>\n	<li>\n		1 tbs margarine, melted</li>\n	<li>\n		75 g Cheddar cheese, grated</li>\n	<li>\n		100 g KEWPIE Spreads (All Chocolate Variants)</li>\n</ul>\n', '3d361-martabak-mini.jpg', NULL, NULL, '771cf-martabak-manis.jpg', '<div>\n	Homemade sweet martabak with chocolate flavor will be different from any martabak you meet outside.</div>\n<div>\n	Treat your family and friends with this sweetness together.</div>\n', NULL, 'no', NULL, '15', '30', '<p>\n	Bake skin :</p>\n<ol>\n	<li>\n		sieve the flour and baking powder.</li>\n	<li>\n		Add 25 gram sugar and salt. Stir well.</li>\n	<li>\n		Pour a little water then gently mix.Let it for 20 minutes.</li>\n	<li>\n		Add the eggs, baking soda, and 25 g sugar, beat.</li>\n	<li>\n		Add melted margarine and stir well.</li>\n	<li>\n		Pour batter into mini martabak mold (lined with margarine) and reheat. Sprinkle with the remaining sugar. Let the dough cooked, cover and lift.</li>\n	<li>\n		Spread margarine over the surface of the cake. Sprinkle grated cheddar cheese on top, then add KEWPIE Spreads.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(35, 1, NULL, 'Mayonnaise Omelette Fried Rice ', 'mayonnaise-omelette-fried-rice', NULL, '<div>\n	For Eggs :</div>\n<ul>\n	<li>\n		2 eggs beaten and made to omelet 20cm width</li>\n	<li>\n		4 tbsp KKEWPIE MAYONNAISE ORIGINAL</li>\n</ul>\n<div>\n	&nbsp;</div>\n<div>\n	For Rice</div>\n<ul>\n	<li>\n		400 grams White Rice</li>\n	<li>\n		50 grams Peeled Small Shrimp</li>\n	<li>\n		3 tbs sweet soy sauce</li>\n	<li>\n		4 meatballs each cut in half then slice</li>\n	<li>\n		1 leek, finely sliced</li>\n	<li>\n		1 tbsp KEWPIE MAYONNAISE ORIGINAL for frying</li>\n	<li>\n		Ground spices</li>\n	<li>\n		3 cloves Onion</li>\n	<li>\n		3 cloves Garlic</li>\n	<li>\n		Salt to taste</li>\n</ul>\n', '1c27d-food-kewpie_tumisayamwijen_3.jpg', NULL, NULL, 'bf623-mayo-original.jpg', '<div>\n	Add mayonnaise in to the eggfor a creamy texture, even when cold.</div>\n<div>\n	Mayonnaise that mixed into rice also makes it more tasty.</div>\n<div>\n	Create lining using mayonnaise to make it look more beautiful.</div>\n', NULL, 'no', NULL, '2', '15', '<ol>\n	<li>\n		Heat the pan, put KEWPIE MAYONNAISE ORIGINAL</li>\n	<li>\n		Stir-fry spices until smells aromatic.&nbsp;</li>\n	<li>\n		Put in shrimp, stir-fry until it changes color, then add the meatballs. Stir well.</li>\n	<li>\n		Add rice, soy sauce, garlics and onions. Stir until cooked</li>\n	<li>\n		Take omelet, fill with rice and fold.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(36, 1, NULL, 'Christmas Tree Broccoli Potato Salad', 'christmas-tree-broccoli-potato-salad', NULL, '<ul>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Broccoli</li>\n	<li>\n		3<span class="Apple-tab-span" style="white-space:pre"> </span>Potatoes</li>\n	<li>\n		&frac12;<span class="Apple-tab-span" style="white-space:pre"> </span>Yellow Paprika<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		2<span class="Apple-tab-span" style="white-space:pre"> </span>Tomatoes ( medium size)</li>\n	<li>\n		6 sheets<span class="Apple-tab-span" style="white-space:pre"> </span>Smoked Beef</li>\n	<li>\n		Salt &amp; Pepper<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		Canned mushroom<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		4 tbs<span class="Apple-tab-span" style="white-space:pre"> </span>KEWPIE MAYONNAISE ORIGINAL</li>\n</ul>\n', '91258-salad-kentang-brokoli-pohon-natal.jpg', NULL, NULL, '761eb-mayo-original.jpg', NULL, NULL, 'no', NULL, '2', '30', '<div>\n	<span style="font-size: 12px;">Potato Salad</span><span style="font-size:12px;">&nbsp;:</span></div>\n<ol>\n	<li>\n		Wash potatoes, wrap while wet in a kitchen towel, then re-wrap with plastic wrap. Heat in microwave (800 W) for 6 minutes. Once cooked, remove and peel off the skin. Mash with a fork while hot.</li>\n	<li>\n		Mix potato salad with 4 tbsp KEWPIE MAYONNAISE ORIGINAL, salt and pepper.&nbsp;</li>\n</ol>\n<p>\n	<span style="font-size: 12px;">Tree</span><span style="font-family:arial,helvetica,sans-serif;">:</span></p>\n<ol>\n	<li>\n		Arrange 2/3 mashed potato on the plate, forming a cone. Arrange broccoli on top of it.&nbsp;</li>\n	<li>\n		Roll smoked beef, create into a rose. Cut paprika into stars using cookie cutter. Arrange on top of broccoli.</li>\n	<li>\n		Decorate your Christmas potato and broccoli tree with mayonnaise.</li>\n</ol>\n<p>\n	<span style="font-size: 12px;">Santa Claus</span>:</p>\n<ol>\n	<li>\n		Cut tomato 1:3, dredge the inside, fill with the remaining potato salad, decorate as you like.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(37, 6, NULL, 'Spicy Tomato Baked Chicken', 'spicy-tomato-baked-chicken', NULL, '<div>\n	Main Ingredients:</div>\n<ul>\n	<li>\n		1 whole chicken</li>\n	<li>\n		2 limes</li>\n	<li>\n		3 cloves garlic</li>\n	<li>\n		KEWPIE Salad Dressing Spicy Tomato&nbsp;</li>\n</ul>\n<div>\n	&nbsp;</div>\n<div>\n	Extra Ingredients:</div>\n<ul>\n	<li>\n		A handful of broccoli, boiled&nbsp;</li>\n	<li>\n		5 baby tomatoes, boiled&nbsp;</li>\n	<li>\n		6 cherry tomatoes</li>\n	<li>\n		&frac12; red paprika</li>\n	<li>\n		&frac12; yellow paprika</li>\n	<li>\n		&frac12; onion, thin sliced</li>\n</ul>\n', 'dcef4-natal_1_ayam_panggang.jpg', NULL, NULL, '7fe07-tomat-pedas.jpg', NULL, NULL, 'no', NULL, '6', '60', '<ol>\n	<li>\n		Marinate chicken with lime juice and garlic, let stand for 15 minutes.</li>\n	<li>\n		Place the chicken in a baking dish, add KEWPIE Salad Dressing Spicy Tomato&nbsp;</li>\n	<li>\n		Thrust the chicken with a fork to allow the flavors to seep, let stand for one night.&nbsp;</li>\n	<li>\n		Bake in the oven for 60 minutes, with temperature 150&deg;c until finely cooked.</li>\n	<li>\n		Put chicken on a dish, add extra vegetable.</li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(38, 7, NULL, 'Chicken Caesar Salad', 'chicken-caesar-salad', NULL, '<ul>\n	<li>\n		300 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Chicken Fillet<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		1 pack<span class="Apple-tab-span" style="white-space:pre"> </span>Mixed Salad</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>kyuri, cut into 2 then slice</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>carrot</li>\n	<li>\n		10 <span class="Apple-tab-span" style="white-space:pre"> </span>cherry tomatoes</li>\n	<li>\n		Dil leaves</li>\n	<li>\n		Salt and pepperKEWPIE Salad Dressing Caesar&nbsp;</li>\n</ul>\n', 'c5b59-natal_6_chicken_salad.jpg', NULL, NULL, '1a0f2-caesar.jpg', NULL, NULL, 'no', NULL, '6', '15', '<ol>\n	<li>\n		Marinate the chicken with salt and pepper, boiled until cooked. Remove and set aside then diced.</li>\n	<li>\n		Thinly sliced carrots, cut into stars using cookie cutter. Boil briefly, remove and drain.</li>\n	<li>\n		Arrange mixed salad, chicken and other vegetable on a dish.</li>\n	<li>\n		Serve with KEWPIE Salad Dressing Caesar as dressing sauce.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(39, 2, NULL, 'Fruit Salad in A Bowl', 'fruit-salad-in-a-bowl', NULL, '<ul>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>Cantaloupe</li>\n	<li>\n		200 gr <span class="Apple-tab-span" style="white-space:pre"> </span>Strawberries</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Dragon Fruit</li>\n	<li>\n		100 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Canned oranges</li>\n	<li>\n		200 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Red grapes</li>\n	<li>\n		200 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Green grapes</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>Pineapple</li>\n	<li>\n		KEWPIE MAYONNAISE ORIGINAL SWEET</li>\n</ul>\n<div>\n	&nbsp;</div>\n', 'bb39a-natal_5_salad_buah.jpg', NULL, NULL, '5bbb4-mayo-manis.jpg', NULL, NULL, 'no', NULL, '6', '20', '<ol>\n	<li>\n		Cut the cantaloupe a little over halfway up. Using a melon baller, scoop out the flesh of the melon. Place it in another bowl.&nbsp;</li>\n	<li>\n		Cut the fruit into small sizes.</li>\n	<li>\n		Put the fruits inside cantaloupe bowl.</li>\n	<li>\n		Add KEWPIE MAYONNAISE ORIGINAL SWEET, then serve</li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(40, 3, NULL, 'Fried Platters', 'fried-platters', NULL, '<div>\n	&nbsp;</div>\n<div>\n	<div style="font-size: 12px;">\n		Tofu Nugget:</div>\n	<div style="font-size: 12px;">\n		<ul>\n			<li>\n				300 gr<span class="Apple-tab-span" style="white-space: pre;"> </span>Tofu</li>\n			<li>\n				200 gr<span class="Apple-tab-span" style="white-space: pre;"> </span>Minced chicken</li>\n			<li>\n				2&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>eggs</li>\n			<li>\n				1 tbs<span class="Apple-tab-span" style="white-space: pre;"> </span>flour</li>\n			<li>\n				5 sticks<span class="Apple-tab-span" style="white-space: pre;"> </span>Beans</li>\n			<li>\n				1&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Carrot</li>\n			<li>\n				Salt &amp; Pepper</li>\n		</ul>\n		<div style="font-size: 12px;">\n			<p>\n				Ebi Fry:</p>\n			<ul>\n				<li>\n					10&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Shrimps</li>\n				<li>\n					150 gr&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Tempura flour</li>\n				<li>\n					150 ml&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Ice cold water</li>\n				<li>\n					Cooking oil</li>\n				<li>\n					KEWPIE MAYONNAISE ORIGINAL&nbsp;</li>\n				<li>\n					KEWPIE SAUS MAYO GURIH&nbsp;</li>\n				<li>\n					KEWPIE Salad Dressing Spicy Tomato</li>\n			</ul>\n		</div>\n		<p style="font-size: 12px;">\n			Ekkado:</p>\n		<ul style="font-size: 12px;">\n			<li>\n				100 gr<span class="Apple-tab-span" style="white-space: pre;"> </span>Minced Chicken</li>\n			<li>\n				100 gr&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Minced shrimps</li>\n			<li>\n				1&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Carrot</li>\n			<li>\n				1&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>leek</li>\n			<li>\n				1&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>egg</li>\n			<li>\n				10&nbsp;<span class="Apple-tab-span" style="white-space: pre;"> </span>Spring Roll wrappers</li>\n		</ul>\n	</div>\n</div>\n<p>\n	&nbsp;</p>\n', 'b3e00-natal_4_aneka_gorengan.jpg', NULL, NULL, 'ea9d2-fried-platters.jpg', NULL, NULL, 'no', NULL, '10', '20', '<div>\n	Tofu Nugget:</div>\n<ol>\n	<li>\n		Mix tofu, 1 egg and other ingredients, put pepper and salt.</li>\n	<li>\n		Put all in a baking dish, steamed until cooked. Lift, cut into dice.</li>\n	<li>\n		Prepare 1 egg in a bowl, whip. Put nugget mixture into the egg, roll into coarse bread crumbs.</li>\n	<li>\n		Repeat once more. Fry until cooked.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	Ekkado:</div>\n<ol>\n	<li>\n		Mix all ingredients until even.</li>\n	<li>\n		Take spring roll wrapper, put stuffing in the middle, then bond the edge in the middle.</li>\n	<li>\n		Tie with string, fry in hot oil until brownish cooked, remove from heat.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	Ebi Fry:</div>\n<ol>\n	<li>\n		Prepare 50 g tempura flour in a bowl, roll shrimp on it.</li>\n	<li>\n		Mix and stir 100 g tempura flour with ice water in a separate container.</li>\n	<li>\n		Dip shrimp into the flour mixture, fry in hot oil until cooked, lift.</li>\n	<li>\n		Serve with KEWPIE MAYONNAISE ORIGINAL , KEWPIE SAUS MAYO GURIH, KEWPIE Salad Dressing Spicy Tomato.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(41, 6, NULL, 'Spicy Tomato Chicken Pasta Salad', 'spicy-tomato-chicken-pasta-salad', NULL, '<ul>\n	<li>\n		150 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Pasta Farfalle</li>\n	<li>\n		200 gr <span class="Apple-tab-span" style="white-space:pre"> </span>Chicken Fillet<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		200 gr <span class="Apple-tab-span" style="white-space:pre"> </span>Broccoli</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>Yellow Paprika</li>\n	<li>\n		8 <span class="Apple-tab-span" style="white-space:pre"> </span>Cherry tomatoes</li>\n	<li>\n		1 pack <span class="Apple-tab-span" style="white-space:pre"> </span>Green salad</li>\n	<li>\n		Salt and Pepper</li>\n	<li>\n		Cooking oil</li>\n	<li>\n		KEWPIE MAYONNAISE ORIGINAL&nbsp;</li>\n	<li>\n		KEWPIE Salad Dressing Spicy Tomato</li>\n</ul>\n<p>\n	&nbsp;</p>\n', '84411-natal_8_salad_pasta_ayam.jpg', NULL, NULL, '195e6-salad-pasta-ayam-tomat-pedas.jpg', NULL, NULL, 'no', '6ade8-resep-cny-kiri.jpg', '5', '20', '<ol>\n	<li>\n		Marinate the chicken with salt and pepper, boiled until cooked. Remove and drain</li>\n	<li>\n		Boil water, add cooking oil, add pasta and cook until done. Remove and drain.</li>\n	<li>\n		Heat the pan, pour KEWPIE Salad Dressing Spicy Tomato. Add pasta, and cook until absorbed, lift.</li>\n	<li>\n		Clean broccoli, cut per floret. Boil until cooked, remove and drain&nbsp;</li>\n	<li>\n		Arrange lettuce on a plate, add the other ingredients.</li>\n	<li>\n		Serve with KEWPIE MAYONNAISE ORIGINAL&nbsp;</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(42, 6, NULL, 'Spicy Tomato Baked Macaroni', 'spicy-tomato-baked-macaroni', NULL, '<ul>\n	<li>\n		2 tbs<span class="Apple-tab-span" style="white-space:pre"> </span>Margarine</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>Onion</li>\n	<li>\n		3 cloves<span class="Apple-tab-span" style="white-space:pre"> </span>Garlic</li>\n	<li>\n		100 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Minced meat</li>\n	<li>\n		100 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Macaroni</li>\n	<li>\n		50 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Mix vegetable</li>\n	<li>\n		50 gr <span class="Apple-tab-span" style="white-space:pre"> </span>champignon mushrooms</li>\n	<li>\n		5<span class="Apple-tab-span" style="white-space:pre"> </span>eggs</li>\n	<li>\n		300 ml<span class="Apple-tab-span" style="white-space:pre"> </span>Milk</li>\n	<li>\n		50 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Cheddar cheese&nbsp;</li>\n	<li>\n		Cooking oil</li>\n	<li>\n		KEWPIE Salad Dressing Spicy Tomato</li>\n</ul>\n', '04b8d-natal_7_macaroni.jpg', NULL, NULL, 'e034c-tomat-pedas.jpg', NULL, NULL, 'no', NULL, '8', '45', '<ol>\n	<li>\n		Boil water and add some oil, put the macaroni, boil for 15 minutes or until done. Remove and drain.</li>\n	<li>\n		Heat margarine to saute onion and garlic until fragrant.</li>\n	<li>\n		Put minced meat and cook until it changes color.</li>\n	<li>\n		Add mixed vegetable and champignon mushrooms, stir well.</li>\n	<li>\n		Add KEWPIE Salad Dressing Spicy Tomato, stir, lift.</li>\n	<li>\n		Put meat in a bowl, add macaroni, eggs and milk, stir until blended.</li>\n	<li>\n		Put mixture into a heat resistant dish that has been spread with margarine, sprinkle with grated cheese.</li>\n	<li>\n		Bake at 150 &deg; C for about 30 minutes or until cooked.</li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL);
INSERT INTO `tbl_product_recipes` (`id`, `product_id`, `name`, `subject`, `url`, `synopsis`, `text`, `media`, `video`, `background`, `cover`, `attribute`, `allow_comment`, `favorited`, `favorite_image`, `served`, `time`, `messages`, `tags`, `order`, `in_front_order`, `user_id`, `count`, `in_front`, `status`, `added`, `modified`) VALUES
(43, 1, NULL, 'Various Canapés', 'various-canaps', NULL, '<div>\n	Chicken Nugget Canap&eacute;:</div>\n<ul>\n	<li>\n		4 Slices<span class="Apple-tab-span" style="white-space:pre"> </span>white bread</li>\n	<li>\n		16 pieces<span class="Apple-tab-span" style="white-space:pre"> </span>Chicken Nugget Coin</li>\n	<li>\n		Green leaf, ripped</li>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>carrot, grated and boiled&nbsp;</li>\n	<li>\n		Cooking oil</li>\n	<li>\n		KEWPIE SANDWICH SPREAD ORIGINAL/ KEWPIE SANDWICH SPREAD CHEESE</li>\n</ul>\n<p>\n	Mayonnaise Chicken Canap&eacute;:</p>\n<ul>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>Kyuri, thinly sliced</li>\n	<li>\n		8 <span class="Apple-tab-span" style="white-space:pre"> </span>Smoked chicken breast</li>\n	<li>\n		4<span class="Apple-tab-span" style="white-space:pre"> </span>Cherry tomatoes, halved</li>\n	<li>\n		KEWPIE MAYONNAISE ORIGINAL</li>\n</ul>\n<p>\n	Veggie Chicken Pie:</p>\n<ul>\n	<li>\n		10 <span class="Apple-tab-span" style="white-space:pre"> </span>Pie shells</li>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>Onion, minced&nbsp;</li>\n	<li>\n		2 cloves <span class="Apple-tab-span" style="white-space:pre"> </span>garlic, minced</li>\n	<li>\n		200 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Chicken fillet, minced</li>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>Carrot, diced</li>\n	<li>\n		5 <span class="Apple-tab-span" style="white-space:pre"> </span>Beans, sliced</li>\n	<li>\n		Cooking oil</li>\n	<li>\n		KEWPIE &nbsp;SAUS MAYO GURIH</li>\n</ul>\n<div>\n	&nbsp;</div>\n<div>\n	Spicy Tomato Scallop:</div>\n<div>\n	<ul>\n		<li>\n			200 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Scallop</li>\n		<li>\n			1 <span class="Apple-tab-span" style="white-space:pre"> </span>red pepper, minced</li>\n		<li>\n			KEWPIE Salad Dressing Spicy Tomato</li>\n	</ul>\n</div>\n<p>\n	&nbsp;</p>\n', '86e3e-aneka-canape.jpg', NULL, NULL, 'd833b-mayo-gurih.jpg', NULL, NULL, 'no', NULL, '10', '20', '<div>\n	Vegetable Chicken Pie:</div>\n<ol>\n	<li>\n		Saute onion and garlic in a frying pan until fragrant, put chicken fillet, cook until it changes color.</li>\n	<li>\n		Add vegetables and cook until done, remove from heat.</li>\n	<li>\n		Add KEWPIE &nbsp;SAUS MAYO GURIH, stir evenly.</li>\n	<li>\n		Put the dough over pie shell and serve.&nbsp;</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	Spicy Tomato Scallop:</div>\n<ol>\n	<li>\n		Marinate scallops for approximately 1 hour with KEWPIE Salad Dressing Spicy Tomato, bake until done.</li>\n	<li>\n		Serve with minced red peppers and KEWPIE Salad Dressing Spicy Tomato.</li>\n</ol>\n<div>\n	&nbsp;</div>\n<div>\n	Chicken Nugget Canap&eacute;:</div>\n<ol>\n	<li>\n		Cut slice of bread into small rectangular shape, put lettuce on it.</li>\n	<li>\n		Arrange chicken nugget on it, add KEWPIE &nbsp;SAUS MAYO GURIH on top, and sprinkle grated carrot</li>\n</ol>\n<p>\n	Mayonnaise Chicken Canap&eacute; :</p>\n<ol>\n	<li>\n		Grill smoked chicken breast on a non-stick frying pan until both sides all cooked, remove from heat, let it cool.</li>\n	<li>\n		<span style="font-size: 12px;">Put on kyuri slices, add KEWPIE MAYONNAISE ORIGINAL and cherry tomatoes on top.&nbsp;</span></li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(44, 1, NULL, 'Sausage and Grilled Corn Mayonnaise', 'sausage-and-grilled-corn-mayonnaise', NULL, '<ul>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>Corn</li>\n	<li>\n		2 <span class="Apple-tab-span" style="white-space:pre"> </span>Beef sausages</li>\n	<li>\n		8 <span class="Apple-tab-span" style="white-space:pre"> </span>Shrimps</li>\n	<li>\n		8 <span class="Apple-tab-span" style="white-space:pre"> </span>Baby potatoes</li>\n	<li>\n		8 <span class="Apple-tab-span" style="white-space:pre"> </span>Cherry tomatoes</li>\n</ul>\n<div>\n	<div>\n		Seasoning:</div>\n	<ul>\n		<li>\n			2 tsp<span class="Apple-tab-span" style="white-space:pre"> </span>black pepper</li>\n		<li>\n			salt</li>\n		<li>\n			1 tsp<span class="Apple-tab-span" style="white-space:pre"> </span>minced parsley</li>\n		<li>\n			KEWPIE MAYONNAISE ORIGINAL</li>\n	</ul>\n</div>\n<p>\n	&nbsp;</p>\n', '7a33f-new_year_6_jagung_sosis_bakar.jpg', NULL, NULL, '88099-mayo-original.jpg', NULL, NULL, 'no', '2f498-resep-cny-kanan.jpg', '8', '25', '<ol>\n	<li>\n		Cut corns as you like, cut sausage into four.</li>\n	<li>\n		Steam potatoes until half done, lift.</li>\n	<li>\n		Make skewers with this order: corn, sausage, shrimp, potato, cherry tomato.</li>\n	<li>\n		Mix and blend KEWPIE MAYONNAISE ORIGINAL, black pepper, salt and parsley.</li>\n	<li>\n		Season skewers, grill while keep seasoning until well done.</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(45, 5, NULL, 'Beef Salad', 'beef-salad', NULL, '<ul>\n	<li>\n		300 gr<span class="Apple-tab-span" style="white-space:pre"> </span>Beef tenderloin</li>\n	<li>\n		1 pack<span class="Apple-tab-span" style="white-space:pre"> </span>green Salad</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>Unripe mango</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>kyuri</li>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>Red Onion</li>\n	<li>\n		4 <span class="Apple-tab-span" style="white-space:pre"> </span>Cayenne pepper</li>\n	<li>\n		4 <span class="Apple-tab-span" style="white-space:pre"> </span>Red Pepper</li>\n	<li>\n		Coriander leaves</li>\n	<li>\n		KEWPIE Salad Dressing Soy Sauce</li>\n</ul>\n', '7f3a2-new_year_1_salad_daging.jpg', NULL, NULL, 'd0bcd-kecap-ala-jepang.jpg', '<p>\n	Cut out cayenne and red pepper if you prefer less spicy taste.</p>\n', NULL, 'no', NULL, '6', '20', '<ol>\n	<li>\n		Marinate meat with KEWPIE Salad Dressing Soy Sauce, let stand for approximately 1 hour, roast until cooked and then lift.&nbsp;</li>\n	<li>\n		Cut mango and kyuri into small pieces.</li>\n	<li>\n		Thinly slice meat. Arrange green salad, mango, kyuri and red onion on a dish.</li>\n	<li>\n		Mix cayenne pepper and red pepper with KEWPIE Salad Dressing Soy Sauce, pour over the salad. Serve.&nbsp;</li>\n</ol>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(46, 4, NULL, 'Fried Sushi with Roasted Sesame', 'fried-sushi-with-roasted-sesame', NULL, '<ul>\n	<li>\n		300 gr<span class="Apple-tab-span" style="white-space:pre"> </span>rice&nbsp;</li>\n	<li>\n		2 sheets<span class="Apple-tab-span" style="white-space:pre"> </span>Nori</li>\n	<li>\n		50 gr<span class="Apple-tab-span" style="white-space:pre"> </span>flour&nbsp;</li>\n	<li>\n		2<span class="Apple-tab-span" style="white-space:pre"> </span>eggs</li>\n	<li>\n		200 gr<span class="Apple-tab-span" style="white-space:pre"> </span>bread crumbs</li>\n	<li>\n		Cooking oil</li>\n	<li>\n		Filling&nbsp;</li>\n	<li>\n		150 gr<span class="Apple-tab-span" style="white-space:pre"> </span>chicken fillet, thinly sliced</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>carrot</li>\n	<li>\n		1 <span class="Apple-tab-span" style="white-space:pre"> </span>kyuri</li>\n	<li>\n		KEWPIE Salad Dressing Roasted Sesame</li>\n	<li>\n		KEWPIE MAYONNAISE ORIGINAL</li>\n</ul>\n', 'ef056-new_year_3_sushi_goreng.jpg', NULL, NULL, 'd51e1-sushi-goreng-wijen-sangrai.jpg', NULL, NULL, 'no', NULL, '20', '15', '<ol>\n	<li>\n		Saute chicken until it changes color, add Kewpie Salad Dressing Roasted Sesame, cook until the spices infused. Lift, set aside.</li>\n	<li>\n		Cut long carrots and kyuri, boiled carrots until soft, remove from heat.</li>\n	<li>\n		Take 1 sheet of nori, place it on sushi mat, put the rice on top Add sauteed chicken, carrots, and kyuri, roll with a little pressure until solid.</li>\n	<li>\n		Roll sushi rolls above the flour, put in beaten eggs, roll again in bread crumbs. Fry in hot oil until cooked, remove and drain.</li>\n	<li>\n		Serve with KEWPIE MAYONNAISE ORIGINAL and nori pieces on the top of it.</li>\n</ol>\n<div>\n	&nbsp;</div>\n', NULL, NULL, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(47, 4, NULL, 'Yee Sang', 'yee-sang', '<p>\n	Thinly slice zucchini, carrot, radish, and j&iacute;cama.Add green and red food coloring to bengkoang</p>\n<p>\n	Decorate all ingredients on a plate with the salmon in the center.</p>\n<p>\n	Serve with KEWPIE Salad Dressing Roasted Sesame and KEWPIE Salad Dressing Soy Sauce as dipping sauce</p>\n', '<ul>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Zucchini</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Carrot</li>\n	<li>\n		50 g<span class="Apple-tab-span" style="white-space:pre"> </span>Japanese red ginger</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Radish</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>J&iacute;cama</li>\n	<li>\n		200 g <span class="Apple-tab-span" style="white-space:pre"> </span>Pomelo</li>\n	<li>\n		100 g<span class="Apple-tab-span" style="white-space:pre"> </span>Smoked salmon</li>\n	<li>\n		Green &amp; red food coloring</li>\n	<li>\n		KEWPIE Salad Dressing Roasted Sesame</li>\n	<li>\n		KEWPIE Salad Dressing Soy Sauce</li>\n</ul>\n', '12aae-yee-sang-[2315x1736].jpg', NULL, NULL, NULL, '<div>\n	Smoked salmon can be replaced with fresh salmon or sauted salmon.</div>\n', NULL, 'yes', '409ea-resep-cny-kanan.jpg', '4', '10', '<ol>\n	<li>\n		Thinly slice zucchini, carrot, radish, and j&iacute;cama.</li>\n	<li>\n		Add green and red food coloring to bengkoang</li>\n	<li>\n		Decorate all ingredients on a plate with the salmon in the center.</li>\n	<li>\n		Serve with KEWPIE Salad Dressing Roasted Sesame and KEWPIE Salad Dressing Soy Sauce as dipping sauce</li>\n</ol>\n', NULL, 2, 2, NULL, NULL, 'yes', 'publish', NULL, NULL),
(48, 5, NULL, 'Chicken Noodle with Mushroom', 'chicken-noodle-with-mushroom', '<p>\n	Stir-fry the ground ingredients until fragrant, put chicken. Cook until the color changes.</p>\n<p>\n	Add KEWPIE Salad Dressing Soy Sauce,and Put noodle and vegetables, stir well until cooked.</p>\n<p>\n	Serve noodle in a bowl and sprinkle with sesame. Serve while it&#39;s hot.</p>\n', '<ul>\n	<li>\n		400 g<span class="Apple-tab-span" style="white-space:pre"> </span>Noodle</li>\n	<li>\n		150 g<span class="Apple-tab-span" style="white-space:pre"> </span>Chicken Fillet</li>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>Carrot</li>\n	<li>\n		100 g <span class="Apple-tab-span" style="white-space:pre"> </span>Cabbage</li>\n	<li>\n		1 pack<span class="Apple-tab-span" style="white-space:pre"> </span>Enoki Mushroom</li>\n	<li>\n		2<span class="Apple-tab-span" style="white-space:pre"> </span>Leek<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n</ul>\n<p>\n	Seasoning ingredients to grind :</p>\n<ul>\n	<li>\n		3 cloves<span class="Apple-tab-span" style="white-space:pre"> </span>Garlic</li>\n	<li>\n		4<span class="Apple-tab-span" style="white-space:pre"> </span>Red chilli pepper</li>\n	<li>\n		KEWPIE Salad Dressing Soy Sauce</li>\n</ul>\n', '4b7d7-mie-ayam-jamur-[2315-x-1736].jpg', NULL, NULL, NULL, NULL, NULL, 'yes', '93ef8-resep-cny-kiri.jpg', '4', '15', '<ol>\n	<li>\n		Stir-fry the ground ingredients until fragrant, put chicken. Cook until the color changes.</li>\n	<li>\n		Add KEWPIE Salad Dressing Soy Sauce, stir well.</li>\n	<li>\n		2.Put noodle and vegetables, stir well until cooked.</li>\n	<li>\n		3.Serve noodle in a bowl and sprinkle with sesame. Serve while it&#39;s hot.</li>\n</ol>\n', NULL, 1, 0, NULL, NULL, 'no', 'publish', NULL, NULL),
(51, 8, NULL, 'Chicken and Vegetable Salad ', 'chicken-and-vegetable-salad', NULL, '<ul>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Lettuce<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		200 g<span class="Apple-tab-span" style="white-space:pre"> </span>Chicken fillet</li>\n	<li>\n		100 g<span class="Apple-tab-span" style="white-space:pre"> </span>Japanese beansprouts</li>\n	<li>\n		&frac12;<span class="Apple-tab-span" style="white-space:pre"> </span>Zucchini</li>\n	<li>\n		&frac12; <span class="Apple-tab-span" style="white-space:pre"> </span>Carrot</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Sweet corn</li>\n	<li>\n		Sliced almonds</li>\n	<li>\n		Red chilli pepper, thinly sliced</li>\n	<li>\n		Margarine<span class="Apple-tab-span" style="white-space:pre"> </span>sufficiently</li>\n	<li>\n		KEWPIE Salad Dressing Thousand Island</li>\n	<li>\n		KEWPIE Salad Dressing Spicy Tomato&nbsp;</li>\n</ul>\n', '1af8e-salad-ayam-dan-selada-[2315x1736].jpg', NULL, NULL, NULL, NULL, NULL, 'no', NULL, '3', '20', '<ol>\n	<li>\n		Marinate chicken fillet with KEWPIE Salad Dressing Spicy Tomato for about an hour.</li>\n	<li>\n		Put margarine in a hot nonstick skillet and pan sear chicken in it until cooked thoroughly.Lift and slice as you like.</li>\n	<li>\n		Boil corn until cooked, lift, shell. Cut the vegetables as you like.</li>\n	<li>\n		Put lettuce, vegetables, red chilli pepper, and sliced almond inside a bowl, pour KEWPIE Salad Dressing Thousand Island. Ready to serve.</li>\n</ol>\n', NULL, 3, 3, NULL, NULL, 'yes', 'publish', NULL, NULL),
(52, 13, NULL, 'Manggo Banana Eggroll with Sweet Sauce', 'manggo-banana-eggroll-with-sweet-sauce', NULL, '<ul>\n	<li>\n		10 pieces <span class="Apple-tab-span" style="white-space:pre"> </span>Spring Roll<span class="Apple-tab-span" style="white-space:pre"> </span> <span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Mango</li>\n	<li>\n		3<span class="Apple-tab-span" style="white-space:pre"> </span>Cooking Plantain</li>\n	<li>\n		Egg white</li>\n	<li>\n		Cooking oil</li>\n	<li>\n		KEWPIE Banana Chocolate Spread&nbsp;</li>\n	<li>\n		KEWPIE Coffee Chocolate Spread</li>\n	<li>\n		KEWPIE Orange Chocolate Spread</li>\n	<li>\n		KEWPIE Vanilla Strawberry Spread</li>\n	<li>\n		KEWPIE Vanilla Pineapple Spread</li>\n</ul>\n<div>\n	&nbsp;</div>\n', '2d342-lumpia-buah-saus-manis-[2315-x-1736].jpg', NULL, NULL, '2fa7a-lumpia-buah-saus-manis-[2315-x-1736].jpg', NULL, NULL, 'no', NULL, '10', '10', '<ol>\n	<li>\n		Cut mango and plantain into 5cm x 2cm sizes, set aside.</li>\n	<li>\n		Take 1 spring roll, put mango and plantain on, fold in the shape of an envelope, and roll up neatly. Glue the tip with egg white.</li>\n	<li>\n		Fry in hot oil until golden brown and cooked. Lift to remove excess oil</li>\n	<li>\n		Serve the eggrolls with your favorite KEWPIE SPREADS</li>\n</ol>\n', NULL, 1, 1, NULL, NULL, 'yes', 'publish', NULL, NULL),
(53, 4, NULL, 'Roasted Sesame Steamed Chicken with Rice', 'roasted-sesame-steamed-chicken-with-rice', NULL, '<ul>\n	<li>\n		400 g <span class="Apple-tab-span" style="white-space:pre"> </span>Rice<span class="Apple-tab-span" style="white-space:pre"> </span></li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Whole chicken breast</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Zucchini&nbsp;</li>\n	<li>\n		2<span class="Apple-tab-span" style="white-space:pre"> </span>Leek</li>\n	<li>\n		1<span class="Apple-tab-span" style="white-space:pre"> </span>Red chilli pepper</li>\n	<li>\n		&frac14;<span class="Apple-tab-span" style="white-space:pre"> </span>Carrot</li>\n	<li>\n		KEWPIE Salad Dressing Roasted Sesame</li>\n</ul>\n<div>\n	&nbsp;</div>\n', '61829-nasi-ayam-kukus-[2315x1736].jpg', NULL, NULL, NULL, NULL, NULL, 'no', NULL, '2', '15', '<ol>\n	<li>\n		Coat chicken breast with KEWPIE Salad Dressing Roasted Sesame, let it rest for 30 minutes, steamed until cooked.Slice as you like.</li>\n	<li>\n		Thinly slice leek and carrot lengthwise. Boil carrot until cooked, lift.</li>\n	<li>\n		Prepare rice on the plate, sprinkle with carrot, serve with sliced chicken and zucchini. Sprinkle leek and chopped red chilli pepper.</li>\n	<li>\n		Pour KEWPIE Salad Dressing Roasted Sesame over the chicken. Serve.</li>\n</ol>\n', NULL, 4, 4, NULL, NULL, 'yes', 'publish', NULL, NULL);
