INSERT INTO `tbl_translations` (`id`, `table`, `field_id`, `lang_id`, `name`, `subject`, `url`, `synopsis`, `text`, `attribute`, `messages`, `user_id`, `count`, `status`, `added`, `modified`) VALUES
(1, 'tbl_pages', 1, 1, NULL, 'Kini KEWPIE Hadir di Indonesia!', 'kini-kewpie-hadir-di-indonesia', NULL, '<div>\n	Gong xi, gong xi! Rayakan tahun baru Imlek ke 2567 dengan aneka kelezatan KEWPIE. Nikmati lezatnya dalam resep-resep spesial Chinese New Year kami.</div>\n', NULL, NULL, 1, NULL, 1, 1432742665, 1454042328),
(2, 'tbl_pages', 8, 1, NULL, 'Mayonais', 'mayonais', '<p>\n	Mayonais</p>\n', '<p>\n	Mayonais</p>\n', NULL, NULL, 1, NULL, 1, 1432742735, 1432742819),
(5, 'tbl_page_menus', 2, 1, NULL, 'Produk Kami', 'produk-kami', NULL, '<p>\n	Mulai dari mayonais, dressing, hingga olesan untuk roti, KEWPIE INDONESIA menghadirkan berbagai jenis produk yang &nbsp;menjadi pelezat hidangan Anda!&nbsp;</p>\n', NULL, NULL, 1, NULL, 1, 1432743088, 1439539269),
(6, 'tbl_page_menus', 3, 1, NULL, 'Di Dalam KEWPIE', 'di-dalam-kewpie', '<h3>\n	Bahan Dasar KEWPIE&nbsp;MAYONAIS</h3>\n<p class="p1">\n	Terbuat dari telur, cuka dan minyak sayur yang diproses dengan teknologi modern KEWPIE, Mayonais KEWPIE diproduksi melalui proses emulsi yang sangat baik. Proses emulsi ini menghasilkan partikel yang lebih kecil dan lebih rapat dibanding produk mayonais lain, sehingga KEWPIE MAYONAIS memiliki rasa yang lembut dan padat.</p>\n', '<h2 class="p1">\n	<strong>Asal Mula Mayonais KEWPIE</strong></h2>\n<p class="p1">\n	Touichiro Nakashima (penemu KEWPIE mayonais) pertama kali mengenal mayonais pada tahun 1912, saat ia berkunjung ke Amerika Serikat untuk mempelajari makanan kaleng. Saat itu, dalam kesehariannya orang Amerika menyantap salad kentang dengan mayonais. Santapan yang lezat dan bergizi ini begitu berkesan bagi Nakashima sehingga ia bertekad untuk menyebarluaskan mayonais di Jepang.</p>\n<p class="p1">\n	Sekembalinya ke Jepang, Nakashima mulai mengembangkan mayonais bergizi tinggi, yang banyak mengandung kuning telur. Mayonais tersebut mulai dijual di Jepang pada tahun 1925 dengan merek KEWPIE. KEWPIE Mayonais merupakan mayonais produksi dalam negeri pertama di Jepang.</p>\n<p class="p1">\n	Pada perkembangannya sekarang, selain mayonais, KEWPIE menyediakan produk-produk makanan yang berkualitas untuk segala usia di berbagai negara di dunia.&nbsp;</p>\n<p class="p1">\n	&nbsp;</p>\n<h2 class="p1">\n	<strong>Bahan Dasar KEWPIE Mayonais</strong></h2>\n<p class="p1">\n	Terbuat dari telur, cuka dan minyak sayur yang diproses dengan teknologi modern KEWPIE, Mayonais KEWPIE diproduksi melalui proses emulsi yang sangat baik. Proses emulsi ini menghasilkan partikel yang lebih kecil dan lebih rapat, dibanding produk mayonais lain. Sehingga KEWPIE mayonais memiliki rasa yang lembut dan padat.</p>\n<p class="p1">\n	&nbsp;</p>\n<h2 class="p1">\n	<strong>Proses Produksi</strong></h2>\n<p class="p1">\n	Semua varian produk dari KEWPIE INDONESIA dibuat dari bahan-bahan berkualitas, diproses dengan baik sehingga menghasilkan produk yang aman dikonsumsi. Dengan tube berstandar pangan, produk-produk Mayonais, Olesan Sandwich serta Saus Manis mudah digunakan dan dibawa. Sangat praktis untuk membuat lining pada makanan atau kreasi gambar pada roti.</p>\n', NULL, NULL, 1, NULL, 1, 1432743463, 1441008600),
